nr_seq_mensalidade_w	bigint;


BEGIN

select	max(nr_seq_mensalidade)
into STRICT	nr_seq_mensalidade_w
from	titulo_receber
where	nr_titulo	= nr_titulo_p;

if (coalesce(nr_seq_mensalidade_w::text, '') = '') then
	CALL wheb_mensagem_pck.exibir_mensagem_abort( 218737, 'NR_TITULO='||nr_titulo_p);
end if;

insert	into	pls_cancel_rec_mensalidade(	nr_sequencia, nr_seq_lote, nr_seq_mensalidade,
		dt_atualizacao, nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec,
		ie_status)
	values (	nextval('pls_cancel_rec_mensalidade_seq'), nr_seq_lote_p, nr_seq_mensalidade_w,
		clock_timestamp(), nm_usuario_p, clock_timestamp(), nm_usuario_p,
		'P');

commit;

end;
