CREATE OR REPLACE PROCEDURE pls_vinc_tit_contest (nr_seq_lote_contest_p bigint, nr_seq_lote_disc_p bigint, nr_titulo_p bigint, ie_opcao_p text, nm_usuario_p text) AS $body$
BEGIN
if (nr_titulo_p IS NOT NULL AND nr_titulo_p::text <> '') and (ie_opcao_p IS NOT NULL AND ie_opcao_p::text <> '') and
	((nr_seq_lote_contest_p IS NOT NULL AND nr_seq_lote_contest_p::text <> '') or (nr_seq_lote_disc_p IS NOT NULL AND nr_seq_lote_disc_p::text <> '')) then
	if (nr_seq_lote_contest_p IS NOT NULL AND nr_seq_lote_contest_p::text <> '') then
		update	titulo_pagar
		set	nr_seq_pls_lote_contest 	= nr_seq_lote_contest_p,
			nm_usuario		= nm_usuario_p,
			dt_atualizacao		= clock_timestamp()
		where	nr_titulo 		= nr_titulo_p;
	elsif (nr_seq_lote_disc_p IS NOT NULL AND nr_seq_lote_disc_p::text <> '') then
		update	titulo_pagar
		set	nr_seq_pls_lote_disc 	= nr_seq_lote_contest_p,
			nm_usuario		= nm_usuario_p,
			dt_atualizacao		= clock_timestamp()
		where	nr_titulo 		= nr_titulo_p;
	end if;

	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
