SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_visualizar_benef_analise ( nr_seq_proposta_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_idade_w			bigint;
ie_exige_analise_w		varchar(10);
nr_seq_beneficiario_w		bigint;
nr_seq_analise_w		bigint;
ie_exige_declaracao_saude_w	varchar(10);
ie_pessoa_contrato_w		varchar(10);
ds_situacao_analise_w		varchar(255);
dt_analise_w			timestamp;
ie_tipo_operacao_w		pls_plano.ie_tipo_operacao%type;
ie_segmentacao_w		pls_plano.ie_segmentacao%type;

C01 CURSOR FOR
	SELECT	a.nr_sequencia,
		coalesce(a.ie_exige_declaracao_saude,'N'),
		obter_idade_pf(a.cd_beneficiario,clock_timestamp(),'A'),
		(	SELECT	max(x.nr_sequencia)
			from	pls_analise_adesao x
			where	x.nr_seq_pessoa_proposta	= a.nr_sequencia) nr_seq_analise,
		b.ie_tipo_operacao,
		b.ie_segmentacao
	from	pls_proposta_beneficiario a,
		pls_plano b
	where	b.nr_sequencia		= a.nr_seq_plano
	and	a.nr_seq_proposta	= nr_seq_proposta_p
	and	coalesce(a.dt_cancelamento::text, '') = '';

C02 CURSOR FOR
	SELECT	ie_exige_analise
	from	pls_regra_exig_analise
	where	cd_estabelecimento	= cd_estabelecimento_p
	and	ie_situacao		= 'A'
	and	qt_idade_w between coalesce(qt_idade_inicial,0) and coalesce(qt_idade_final,999)
	and	((ie_declaracao_saude	= ie_exige_declaracao_saude_w) or (ie_declaracao_saude = 'A'))
	and	((ie_tipo_operacao	= ie_tipo_operacao_w) or (coalesce(ie_tipo_operacao::text, '') = ''))
	and	((ie_tipo_proposta	= ie_pessoa_contrato_w) or (ie_tipo_proposta = 'A'))
	and	((ie_segmentacao	= ie_segmentacao_w) or (coalesce(ie_segmentacao::text, '') = ''))
	order by coalesce(ie_declaracao_saude, 'A'),
		coalesce(ie_segmentacao, ' '),
		coalesce(ie_tipo_operacao, ' '),
		coalesce(ie_tipo_proposta, 'A');


BEGIN

select	CASE WHEN coalesce(cd_cgc_estipulante::text, '') = '' THEN 'PF'  ELSE 'PJ' END
into STRICT	ie_pessoa_contrato_w
from	pls_proposta_adesao
where	nr_sequencia	= nr_seq_proposta_p;

open C01;
loop
fetch C01 into
	nr_seq_beneficiario_w,
	ie_exige_declaracao_saude_w,
	qt_idade_w,
	nr_seq_analise_w,
	ie_tipo_operacao_w,
	ie_segmentacao_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	
	delete	FROM w_pls_prop_benef_analise
	where	nr_seq_benef_proposta	= nr_seq_beneficiario_w;
	
	if (coalesce(nr_seq_analise_w::text, '') = '') then
		dt_analise_w		:= null;
		ds_situacao_analise_w	:= wheb_mensagem_pck.get_texto(1186204); --Analise nao gerada
	else
		select	substr(obter_valor_dominio(2504,ie_status),1,255),
			dt_analise
		into STRICT	ds_situacao_analise_w,
			dt_analise_w
		from	pls_analise_adesao
		where	nr_sequencia	= nr_seq_analise_w;
	end if;
	
	ie_exige_analise_w	:= 'N';
	
	open C02;
	loop
	fetch C02 into
		ie_exige_analise_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
	end loop;
	close C02;
	
	if (ie_exige_analise_w	= 'S') then
		insert into w_pls_prop_benef_analise(	nr_sequencia,dt_atualizacao,nm_usuario,dt_atualizacao_nrec,nm_usuario_nrec,
				nr_seq_benef_proposta,dt_analise,ds_situacao_analise,nr_seq_analise,
				nr_seq_proposta)
		values (	nextval('w_pls_prop_benef_analise_seq'),clock_timestamp(),nm_usuario_p,clock_timestamp(),nm_usuario_p,
				nr_seq_beneficiario_w,dt_analise_w,ds_situacao_analise_w,nr_seq_analise_w,
				nr_seq_proposta_p);
	end if;
	
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_visualizar_benef_analise ( nr_seq_proposta_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) FROM PUBLIC;

