CREATE OR REPLACE PROCEDURE pls_zerar_via_adicional ( nr_seq_segurado_p bigint, nm_usuario_p text) AS $body$
DECLARE


vl_via_adicional_w		pls_segurado_carteira.vl_via_adicional%type;
nr_seq_mensalidade_seg_w	pls_segurado_carteira.nr_seq_mensalidade_seg%type;



BEGIN
select 	vl_via_adicional,
	nr_seq_mensalidade_seg
into STRICT	vl_via_adicional_w,
	nr_seq_mensalidade_seg_w
from	pls_segurado_carteira
where	nr_seq_segurado		= nr_seq_segurado_p;

if (vl_via_adicional_w	<> 0) and (coalesce(nr_seq_mensalidade_seg_w::text, '') = '') then

	update	pls_segurado_carteira
	set	nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp(),
		vl_via_adicional	= 0
	where	nr_seq_segurado		= nr_seq_segurado_p;

	CALL pls_gerar_segurado_historico(	nr_seq_segurado_p, '91', clock_timestamp(), 'Valor anterior: ' || Campo_Mascara_virgula(vl_via_adicional_w),
					'Isenção taxa de emissão do cartão de identificação', null, null, null,
					null, clock_timestamp(), null, null,
					null, null, null, null,
					nm_usuario_p, 'N');

	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
