		and	coalesce(a.ie_suspenso, 'N')		= 'N'
		and	coalesce(a.dt_suspensao::text, '') = ''
		and	coalesce(b.dt_suspensao::text, '') = '';

	else

		select	max(dt_validade_prescr)
		into STRICT	dt_extensao_w
		from	prescr_procedimento a,
			prescr_medica b
		where	a.nr_prescricao			= b.nr_prescricao
		and	a.nr_prescricao_original	= nr_prescricao_original_w
		and	a.nr_seq_anterior		= nr_seq_anterior_w
		and	b.cd_pessoa_fisica		= cd_pessoa_fisica_w
		and	coalesce(a.ie_suspenso, 'N')		= 'N'
		and	coalesce(a.dt_suspensao::text, '') = ''
		and	coalesce(b.dt_suspensao::text, '') = '';

	end if;

	if (nr_prescricao_original_w IS NOT NULL AND nr_prescricao_original_w::text <> '') and (nr_seq_anterior_w IS NOT NULL AND nr_seq_anterior_w::text <> '') then

		update	prescr_procedimento
		set	dt_extensao	= dt_extensao_w,
			nm_usuario	= nm_usuario_p
		where	nr_prescricao	= nr_prescricao_original_w
		and	nr_sequencia	= nr_seq_anterior_w;

		open C07;
		loop
		fetch C07 into
			nr_prescr_w,
			nr_seq_w;
		EXIT WHEN NOT FOUND; /* apply on C07 */
			begin

			update	prescr_procedimento
			set	dt_extensao	= dt_extensao_w,
				nm_usuario	= nm_usuario_p
			where	nr_prescricao	= nr_prescr_w
			and	nr_sequencia	= nr_seq_w;

			end;
		end loop;
		close C07;

	end if;

elsif (upper(nm_tabela_p) = 'PRESCR_RECOMENDACAO') then

	if (nr_atendimento_w IS NOT NULL AND nr_atendimento_w::text <> '') then

		select	max(dt_validade_prescr)
		into STRICT	dt_extensao_w
		from	prescr_recomendacao a,
			prescr_medica b
		where	a.nr_prescricao			= b.nr_prescricao
		and	a.nr_prescricao_original	= nr_prescricao_original_w
		and	a.nr_seq_anterior		= nr_seq_anterior_w
		and	b.nr_atendimento		= nr_atendimento_w
		and	coalesce(a.ie_suspenso, 'N')		= 'N'
		and	coalesce(a.dt_suspensao::text, '') = ''
		and	coalesce(b.dt_suspensao::text, '') = '';

	else

		select	max(dt_validade_prescr)
		into STRICT	dt_extensao_w
		from	prescr_recomendacao a,
			prescr_medica b
		where	a.nr_prescricao			= b.nr_prescricao
		and	a.nr_prescricao_original	= nr_prescricao_original_w
		and	a.nr_seq_anterior		= nr_seq_anterior_w
		and	b.cd_pessoa_fisica		= cd_pessoa_fisica_w
		and	coalesce(a.ie_suspenso, 'N')		= 'N'
		and	coalesce(a.dt_suspensao::text, '') = ''
		and	coalesce(b.dt_suspensao::text, '') = '';

	end if;

	if (nr_prescricao_original_w IS NOT NULL AND nr_prescricao_original_w::text <> '') and (nr_seq_anterior_w IS NOT NULL AND nr_seq_anterior_w::text <> '') then

		update	prescr_recomendacao
		set	dt_extensao	= dt_extensao_w,
			nm_usuario	= nm_usuario_p
		where	nr_prescricao	= nr_prescricao_original_w
		and	nr_sequencia	= nr_seq_anterior_w;

		open C08;
		loop
		fetch C08 into
			nr_prescr_w,
			nr_seq_w;
		EXIT WHEN NOT FOUND; /* apply on C08 */
			begin

			update	prescr_recomendacao
			set	dt_extensao	= dt_extensao_w,
				nm_usuario	= nm_usuario_p
			where	nr_prescricao	= nr_prescr_w
			and	nr_sequencia	= nr_seq_w;

			end;
		end loop;
		close C08;

	end if;

end if;

if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;

end;
