SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE plt_ajustar_nova_prescr ( nr_prescricao_p bigint, nr_prescr_orig_p bigint, ie_substituir_p text, nm_usuario_p text) AS $body$
DECLARE


dt_validade_prescr_w		timestamp;
dt_inicio_prescr_w		timestamp;
dt_prescricao_w			timestamp;
dt_primeiro_horario_w		timestamp;
dt_validade_prescr_orig_w	timestamp;
nr_horas_validade_w		integer;


BEGIN

select	dt_prescricao,
	dt_inicio_prescr,
	dt_validade_prescr,
	dt_primeiro_horario,
	nr_horas_validade
into STRICT	dt_prescricao_w,
	dt_inicio_prescr_w,
	dt_validade_prescr_w,
	dt_primeiro_horario_w,
	nr_horas_validade_w
from	prescr_medica
where	nr_prescricao = nr_prescricao_p;

if (ie_substituir_p	= 'S') then

	select	dt_validade_prescr
	into STRICT	dt_validade_prescr_orig_w
	from	prescr_medica
	where	nr_prescricao = nr_prescr_orig_p;
/*
	if	(dt_validade_prescr_w > dt_validade_prescr_orig_w) then
		nr_horas_validade_w := nr_horas_validade_w - 1;
	elsif	(dt_validade_prescr_w < dt_validade_prescr_orig_w) then
		nr_horas_validade_w := nr_horas_validade_w + 1;
	else
		nr_horas_validade_w := nr_horas_validade_w;
	end if;

	update	prescr_medica
	set	nr_horas_validade	= nr_horas_validade_w
	where	nr_prescricao		= nr_prescricao_p;*/
else

	if (nr_horas_validade_w	> 24) and (dt_inicio_prescr_w	> dt_primeiro_horario_w) and
		(dt_primeiro_horario_w	> (clock_timestamp() - interval '2 days')) then

		if (to_char(dt_prescricao_w,'hh24') <> '00') then
			dt_primeiro_horario_w	:= trunc(dt_prescricao_w,'hh24') + 1/24;
		else
			dt_primeiro_horario_w	:= dt_prescricao_w;
		end if;

		nr_horas_validade_w	:= trunc(((dt_validade_prescr_w - 1) - dt_prescricao_w) * 24);

		update	prescr_medica
		set	dt_primeiro_horario	= dt_primeiro_horario_w,
			dt_inicio_prescr	= dt_primeiro_horario_w,
			nr_horas_validade	= nr_horas_validade_w
		where	nr_prescricao		= nr_prescricao_p;

	end if;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
