CREATE OR REPLACE PROCEDURE plt_ajustar_prescr_pos_copia ( nr_prescricao_p bigint, ds_log_p text, ie_modificar_p text, cd_perfil_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
ie_prescr_vazia_w		varchar(1);	
						 

BEGIN 
 
ie_prescr_vazia_w	:= obter_se_prescr_vazia(nr_prescricao_p);
 
if (ie_prescr_vazia_w	= 'S') then 
	CALL excluir_prescr_copia(nr_prescricao_p);
else 
	CALL consistir_itens_prescricao(nr_prescricao_p, nm_usuario_p, 'A');
	CALL plt_gerar_horarios_sem_lib(nr_prescricao_p, ie_modificar_p, nm_usuario_p);
	CALL reordenar_medicamento(nr_prescricao_p);
	CALL Reordenar_Materiais_Pos_Copia(nr_prescricao_p);
end if;
 
end;
$body$
