CREATE OR REPLACE PROCEDURE plt_copia_jejum ( nr_prescricao_orig_p bigint, nr_prescricao_nova_p bigint, nr_seq_regra_p bigint, nm_usuario_p text, ie_modificar_p text, nr_seq_jejum_p bigint, cd_perfil_p bigint, cd_estabelecimento_p bigint, ie_estende_inc_p text) AS $body$
DECLARE


dt_inicio_prescr_w		timestamp;
dt_validade_prescr_w		timestamp;
nr_sequencia_w			bigint;
qt_inconsistencia_w		bigint;


BEGIN

select	dt_inicio_prescr,
		dt_validade_prescr
into STRICT	dt_inicio_prescr_w,
		dt_validade_prescr_w
from	prescr_medica
where	nr_prescricao = nr_prescricao_nova_p  LIMIT 1;

CALL PLT_consiste_extensao_item(dt_inicio_prescr_w, dt_validade_prescr_w, nr_prescricao_orig_p, nr_seq_jejum_p, 'J', nr_seq_regra_p, nm_usuario_p, cd_perfil_p, cd_estabelecimento_p);

select	count(nr_sequencia)
into STRICT	qt_inconsistencia_w
from	w_copia_plano
where	nr_prescricao	= nr_prescricao_orig_p
and		nr_seq_item		= nr_seq_jejum_p
and		ie_tipo_item	= 'J'
and		nm_usuario		= nm_usuario_p
and		((ie_permite	= 'N') or (ie_estende_inc_p = 'N'))  LIMIT 1;

if	((qt_inconsistencia_w	= 0) or (ie_modificar_p 	= 'S')) then

	select	nextval('rep_jejum_seq')
	into STRICT	nr_sequencia_w
	;

	insert into rep_jejum(nr_sequencia,
		nr_prescricao,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_tipo,
		nr_seq_objetivo,
		ie_inicio,
		dt_inicio,
		qt_min_ant,
		qt_min_depois,
		dt_evento,
		ds_evento,
		ds_observacao,
		dt_fim,
		qt_hora_ant,
		qt_hora_depois,
		ie_suspenso,
		ie_repete_copia,
		nr_seq_anterior)
	SELECT  nr_sequencia_w,
		nr_prescricao_nova_p,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_tipo,
		nr_seq_objetivo,
		ie_inicio,
		dt_inicio_prescr_w,
		qt_min_ant,
		qt_min_depois,
		dt_evento,
		ds_evento,
		ds_observacao,
		dt_validade_prescr_w,
		qt_hora_ant,
		qt_hora_depois,
		'N',
		ie_repete_copia,
		coalesce(nr_seq_anterior,nr_sequencia)
	from    rep_jejum
	where   nr_sequencia = nr_seq_jejum_p;

	if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
