SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE plt_copia_npt_adulta ( nr_seq_orig_p bigint, nr_prescricao_p bigint, nr_seq_regra_p bigint, ie_modificar_p text, nm_usuario_p text, ds_erro_p INOUT text) AS $body$
DECLARE


nr_seq_nut_pac_w	bigint;
ie_copiar_w		varchar(10);


BEGIN

select	coalesce(max(ie_copiar),'N')
into STRICT	ie_copiar_w
from	rep_regra_copia_crit
where	nr_seq_regra	= nr_seq_regra_p
and	ie_tipo_item	= 'NPA';

if	((ie_copiar_w	= 'S') or (ie_modificar_p = 'S')) then

	Select	nextval('nut_paciente_seq')
	into STRICT	nr_seq_nut_pac_w
	;

	Insert	into Nut_Paciente(
		nr_sequencia,
		nr_atendimento,
		dt_atualizacao,
		nm_usuario,
		qt_peso,
		qt_idade_ano,
		qt_altura_cm,
		nr_seq_fator_ativ,
		nr_seq_fator_stress,
		pr_proteina,
		pr_lipidio,
		pr_carboidrato,
		qt_kcal_total,
		qt_kcal_prot,
		qt_kcal_lipidio,
		qt_kcal_carboidrato,
		qt_kcal_kg,
		qt_grama_prot_kg_dia,
		pr_npt,
		qt_fase_npt,
		qt_gotejamento_npt,
		nr_prescricao,
		ie_bomba_infusao,
		ie_npt_adulta,
		ie_suspenso,
		dt_status,
		ie_status,
		IE_CALC_AUTOMATICO,
		QT_VOLUME_TOTAL)
	SELECT	nr_seq_nut_pac_w,
		nr_atendimento,
		clock_timestamp(),
		nm_usuario_p,
		qt_peso,
		qt_idade_ano,
		qt_altura_cm,
		nr_seq_fator_ativ,
		nr_seq_fator_stress,
		pr_proteina,
		pr_lipidio,
		pr_carboidrato,
		qt_kcal_total,
		qt_kcal_prot,
		qt_kcal_lipidio,
		qt_kcal_carboidrato,
		qt_kcal_kg,
		qt_grama_prot_kg_dia,
		pr_npt,
		qt_fase_npt,
		qt_gotejamento_npt,
		nr_prescricao_p,
		ie_bomba_infusao,
		ie_npt_adulta,
		'N',
		clock_timestamp(),
		'P',
		IE_CALC_AUTOMATICO,
		QT_VOLUME_TOTAL
	from	nut_paciente
	where	nr_sequencia = nr_seq_orig_p;

	Insert	into Nut_Paciente_Elemento(
		nr_sequencia,
		nr_seq_nut_pac,
		nr_seq_elemento,
		dt_atualizacao,
		nm_usuario,
		qt_kcal,
		qt_elemento,
		nr_seq_elem_unid_med,
		nr_seq_elem_mat,
		qt_volume,
		cd_unid_med_prescr,
		qt_prescricao,
		qt_dispensar,
		qt_vol_1_fase,
		qt_vol_2_fase,
		qt_vol_3_fase)
	SELECT	nextval('nut_paciente_elemento_seq'),
		nr_seq_nut_pac_w,
		nr_seq_elemento,
		clock_timestamp(),
		nm_usuario_p,
		qt_kcal,
		qt_elemento,
		nr_seq_elem_unid_med,
		nr_seq_elem_mat,
		qt_volume,
		cd_unid_med_prescr,
		qt_prescricao,
		qt_dispensar,
		qt_vol_1_fase,
		qt_vol_2_fase,
		qt_vol_3_fase
	from	nut_paciente_elemento
	where	nr_seq_nut_pac = nr_seq_orig_p;

	commit;
else
	ds_erro_p := wheb_mensagem_pck.get_texto(278175);
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
