SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE plt_copia_npt_adul_prot ( nr_seq_orig_p bigint, nr_prescricao_p bigint, nr_seq_regra_p bigint, nm_usuario_p text, ie_tipo_item_p text, ie_modificar_p text, cd_perfil_p bigint, cd_estabelecimento_p bigint, ie_estende_inc_p text) AS $body$
DECLARE


nr_seq_pac_elem_mat_w	bigint;
qt_dia_npt_w		bigint;
nr_nut_pac_w		bigint;
nut_pac_elem_w		bigint;
nr_seq_pac_elem_w	bigint;
dt_inicio_prescr_w		timestamp;
dt_validade_prescr_w	timestamp;
nr_horas_validade_w	prescr_medica.nr_horas_validade%type;
nr_prescr_orig_w		bigint;
qt_inconsistencia_w	bigint;
ie_npt_w			varchar(10);
ie_horas_fixas_npt_w	varchar(1);
qt_hora_inf_w		nut_pac.qt_hora_inf%type;

c03 CURSOR FOR
SELECT	nr_sequencia
from	nut_pac_elemento
where	nr_seq_nut_pac	= nr_seq_orig_p
order by nr_sequencia;

c04 CURSOR FOR
SELECT	nr_sequencia
from	nut_pac_elem_mat
where	nr_seq_nut_pac	= nr_seq_orig_p
order by nr_sequencia;


BEGIN

CALL Wheb_assist_pck.set_informacoes_usuario(cd_estabelecimento_p, cd_perfil_p, nm_usuario_p);
ie_horas_fixas_npt_w 	              :=  Wheb_assist_pck.obterParametroFuncao(924,1149);

select	dt_inicio_prescr,
	dt_validade_prescr,
		nr_horas_validade
into STRICT	dt_inicio_prescr_w,
		dt_validade_prescr_w,
		nr_horas_validade_w
from	prescr_medica
where	nr_prescricao = nr_prescricao_p  LIMIT 1;

select	nr_prescricao,
		CASE WHEN ie_npt_adulta='S' THEN  'NAN' WHEN ie_npt_adulta='N' THEN  'NPN' END
into STRICT	nr_prescr_orig_w,
		ie_npt_w
from	nut_pac
where	nr_sequencia = nr_seq_orig_p  LIMIT 1;

CALL PLT_consiste_extensao_item(dt_inicio_prescr_w, dt_validade_prescr_w, nr_prescr_orig_w, nr_seq_orig_p, ie_npt_w, nr_seq_regra_p, nm_usuario_p, cd_perfil_p, cd_estabelecimento_p);

select	count(nr_sequencia)
into STRICT	qt_inconsistencia_w
from	w_copia_plano
where	nr_prescricao	= nr_prescr_orig_w
and		nr_seq_item	= nr_seq_orig_p
and		ie_tipo_item	= ie_npt_w
and		nm_usuario	= nm_usuario_p
and		((ie_permite	= 'N') or (ie_estende_inc_p = 'N'))  LIMIT 1;

if	((qt_inconsistencia_w	= 0) or (ie_modificar_p 	= 'S')) then

	select	count(distinct(ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(dt_prescricao))) + 1
	into STRICT	qt_dia_npt_w
	from	prescr_medica b,
			nut_pac a
	where	a.nr_prescricao = b.nr_prescricao
	and		ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(b.dt_prescricao) <> ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(clock_timestamp())
	and		b.nr_atendimento = (SELECT obter_atendimento_prescr(nr_prescricao_p) );

	Select	nextval('nut_pac_seq')
	into STRICT	nr_nut_pac_w
	;
	
	qt_hora_inf_w	:= coalesce(nr_horas_validade_w,24);

	Insert into nut_pac(
		cd_intervalo,
		ds_diagnostico,
		ds_justificativa,
		ds_orientacao,
		dt_atualizacao,
		dt_atualizacao_nrec,
		hr_prim_horario,
		ie_ajustar_potassio,
		ie_ajustar_sodio,
		ie_bomba_infusao,
		ie_calcula_vel,
		ie_calculo_auto,
		ie_correcao,
		ie_dispositivo_infusao,
		ie_emissao,
		ie_forma,
		ie_forma_infusao,
		ie_gera_produto,
		ie_npt_adulta,
		ie_permite_alteracao,
		ie_suplementacao,
		ie_suspenso,
		ie_via_administracao,
		nm_usuario,
		nm_usuario_nrec,
		nr_prescricao,
		nr_seq_anterior,
		nr_seq_assinatura,
		nr_seq_fator_ativ,
		nr_seq_fator_stress,
		nr_seq_interf_farm,
		nr_seq_protocolo,
		nr_sequencia,
		pr_carboidrato,
		pr_conc_glic_solucao,
		pr_conc_lipidio_solucao,
		pr_conc_proteina_solucao,
		pr_lipidio,
		pr_npt,
		pr_proteina,
		qt_altura_cm,
		qt_aporte_hidrico_diario,
		qt_dia_npt,
		qt_equipo,
		qt_fase_npt,
		qt_gotejo_npt,
		qt_grama_nitrogenio,
		qt_grama_proteina_kg_dia,
		qt_hora_inf,
		qt_idade_ano,
		qt_idade_dia,
		qt_idade_mes,
		qt_kcal_carboidrato,
		qt_kcal_kg,
		qt_kcal_kg_ped,
		qt_kcal_lipidio,
		qt_kcal_nao_proteico,
		qt_kcal_proteico,
		qt_kcal_proteina,
		qt_kcal_total,
		qt_min_inf,
		qt_multiplicador,
		qt_nec_hidrica_diaria,
		qt_nec_hidrica_diaria_ped,
		qt_nec_kcal_dia,
		qt_nec_kcal_kg_dia,
		qt_osmolaridade_total,
		qt_peso,
		qt_peso_ajustado,
		qt_peso_calorico,
		qt_peso_ideal,
		qt_rel_cal_nit,
		qt_tempo_etapa,
		qt_tmb,
		qt_vel_inf_glicose,
		qt_volume_adep,
		qt_volume_diario,
		dt_status,
		nr_sequencia_anterior,
		ie_editado,
		ie_acm)
	SELECT	cd_intervalo,
		ds_diagnostico,
		ds_justificativa,
		ds_orientacao,
		clock_timestamp(),
		clock_timestamp(),
		hr_prim_horario,
		ie_ajustar_potassio,
		ie_ajustar_sodio,
		ie_bomba_infusao,
		ie_calcula_vel,
		ie_calculo_auto,
		ie_correcao,
		ie_dispositivo_infusao,
		0,
		ie_forma,
		ie_forma_infusao,
		ie_gera_produto,
		ie_npt_adulta,
		ie_permite_alteracao,
		ie_suplementacao,
		'N',
		ie_via_administracao,
		nm_usuario_p,
		nm_usuario_p,
		nr_prescricao_p,
		coalesce(nr_seq_anterior,nr_sequencia),
		nr_seq_assinatura,
		nr_seq_fator_ativ,
		nr_seq_fator_stress,
		nr_seq_interf_farm,
		nr_seq_protocolo,
		nr_nut_pac_w,
		pr_carboidrato,
		pr_conc_glic_solucao,
		pr_conc_lipidio_solucao,
		pr_conc_proteina_solucao,
		pr_lipidio,
		pr_npt,
		pr_proteina,
		qt_altura_cm,
		qt_aporte_hidrico_diario,
		qt_dia_npt_w,
		qt_equipo,
		qt_fase_npt,
		qt_gotejo_npt,
		qt_grama_nitrogenio,
		qt_grama_proteina_kg_dia,
		CASE WHEN ie_horas_fixas_npt_w='S' THEN qt_hora_inf_w  ELSE qt_hora_inf END ,
		qt_idade_ano,
		qt_idade_dia,
		qt_idade_mes,
		qt_kcal_carboidrato,
		qt_kcal_kg,
		qt_kcal_kg_ped,
		qt_kcal_lipidio,
		qt_kcal_nao_proteico,
		qt_kcal_proteico,
		qt_kcal_proteina,
		qt_kcal_total,
		qt_min_inf,
		qt_multiplicador,
		qt_nec_hidrica_diaria,
		qt_nec_hidrica_diaria_ped,
		qt_nec_kcal_dia,
		qt_nec_kcal_kg_dia,
		qt_osmolaridade_total,
		qt_peso,
		qt_peso_ajustado,
		qt_peso_calorico,
		qt_peso_ideal,
		qt_rel_cal_nit,
		qt_tempo_etapa,
		qt_tmb,
		qt_vel_inf_glicose,
		qt_volume_adep,
		qt_volume_diario,
		dt_inicio_prescr_w,
		nr_sequencia,
		ie_editado,
		ie_acm
	from	nut_pac
	where	nr_sequencia	= nr_seq_orig_p;

	open c03;
	loop
	fetch c03 into
		nut_pac_elem_w;
	EXIT WHEN NOT FOUND; /* apply on c03 */
		begin
		select	nextval('nut_pac_elemento_seq')
		into STRICT	nr_seq_pac_elem_w
		;

		Insert into nut_pac_elemento(
			nr_sequencia,
			nr_seq_nut_pac,
			nr_seq_elemento,
			dt_atualizacao,
			nm_usuario,
			cd_unidade_medida,
			qt_elem_kg_dia,
			qt_diaria,
			pr_total,
			qt_kcal,
			ie_prim_fase,
			ie_seg_fase,
			ie_terc_fase,
			ie_quar_fase,
			ie_npt)
		SELECT	nr_seq_pac_elem_w,
			nr_nut_pac_w,
			nr_seq_elemento,
			clock_timestamp(),
			nm_usuario_p,
			cd_unidade_medida,
			qt_elem_kg_dia,
			qt_diaria,
			pr_total,
			qt_kcal,
			ie_prim_fase,
			ie_seg_fase,
			ie_terc_fase,
			ie_quar_fase,
			ie_npt
		from	nut_pac_elemento
		where	nr_sequencia = nut_pac_elem_w;
		end;
	end loop;
	close c03;
	open c04;
	loop
	fetch c04 into
		nr_seq_pac_elem_mat_w;
	EXIT WHEN NOT FOUND; /* apply on c04 */

		insert into nut_pac_elem_mat(
			nr_sequencia,
			nr_seq_pac_elem,
			dt_atualizacao,
			nm_usuario,
			cd_unidade_medida,
			nr_seq_elem_mat,
			qt_volume,
			qt_vol_1_fase,
			qt_vol_2_fase,
			qt_vol_3_fase,
			qt_vol_4_fase,			
			qt_vol_cor,
			nr_seq_nut_pac,
			cd_material,
			qt_protocolo,
			ie_somar_volume,
			qt_dose)
		SELECT	nextval('nut_pac_elem_mat_seq'),
			nr_seq_pac_elem,
			clock_timestamp(),
			nm_usuario_p,
			cd_unidade_medida,
			nr_seq_elem_mat,
			qt_volume,
			qt_vol_1_fase,
			qt_vol_2_fase,
			qt_vol_3_fase,
			qt_vol_4_fase,			
			qt_vol_cor,
			nr_nut_pac_w,
			cd_material,
			qt_protocolo,
			ie_somar_volume,
			qt_dose
		from	nut_pac_elem_mat
		where	nr_sequencia	= nr_seq_pac_elem_mat_w;
		
	end loop;
	close c04;

	if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE plt_copia_npt_adul_prot ( nr_seq_orig_p bigint, nr_prescricao_p bigint, nr_seq_regra_p bigint, nm_usuario_p text, ie_tipo_item_p text, ie_modificar_p text, cd_perfil_p bigint, cd_estabelecimento_p bigint, ie_estende_inc_p text) FROM PUBLIC;

