SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE plt_excluir_pano ( nr_atendimento_p bigint, nm_usuario_p text, cd_pessoa_fisica_p text) AS $body$
DECLARE


nr_prescricao_w		bigint;
nr_sequencia_cirurgia_w	bigint;

BEGIN

if (coalesce(nr_atendimento_p, 0) <> 0) and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then

	select	coalesce(max(a.nr_prescricao),0)
	into STRICT	nr_prescricao_w
	from	prescr_medica a
	where	a.nr_atendimento = nr_atendimento_p
	and	a.nm_usuario_original = nm_usuario_p
	and	coalesce(a.dt_liberacao::text, '') = ''
	and	coalesce(a.dt_liberacao_medico::text, '') = ''
	and not exists (	SELECT	1
         			from   cirurgia x
			where	x.nr_prescricao = a.nr_prescricao
			and    coalesce(a.ie_tipo_prescr_cirur,0) <> 2);

elsif (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then

	select	coalesce(max(a.nr_prescricao),0)
	into STRICT	nr_prescricao_w
	from	prescr_medica a
	where	a.cd_pessoa_fisica = cd_pessoa_fisica_p
	and	coalesce(a.nr_atendimento::text, '') = ''
	and	a.nm_usuario_original = nm_usuario_p
	and	coalesce(a.dt_liberacao::text, '') = ''
	and	coalesce(a.dt_liberacao_medico::text, '') = ''
	and not exists (	SELECT	1
         			from   cirurgia x
			where	x.nr_prescricao = a.nr_prescricao
			and    coalesce(a.ie_tipo_prescr_cirur,0) <> 2);

end if;

if (nr_prescricao_w > 0) then
	delete	from prescr_medica
	where	nr_prescricao = nr_prescricao_w;
	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
