CREATE OR REPLACE PROCEDURE plt_gerar_susp_programada_js ( nr_prescricao_p bigint, nr_seq_item_p bigint, nm_tabela_p text, ie_tipo_item_p text, dt_suspensao_programada_p timestamp, ie_apresentar_mensagem_p INOUT text, nm_usuario_p text) AS $body$
DECLARE

 
ie_apresentar_mensagem_w	varchar(1) := 'N';

BEGIN
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nm_tabela_p IS NOT NULL AND nm_tabela_p::text <> '') then 
	begin 
	 
	if (ie_tipo_item_p = 'P') then 
		begin 
		select	CASE WHEN coalesce(nr_seq_exame,0)=0 THEN 'N'  ELSE 'S' END  
		into STRICT	ie_apresentar_mensagem_w 
		from	prescr_procedimento 
		where	nr_prescricao	= nr_prescricao_p 
		and	nr_sequencia	= nr_seq_item_p;
		end;
	end if;
	 
	if (nm_tabela_p <> '') and (nr_seq_item_p <> '') then 
		begin 
		CALL plt_gerar_suspensao_programada( 
			nr_prescricao_p, 
			nr_seq_item_p, 
			nm_tabela_p, 
			ie_tipo_item_p, 
			nm_usuario_p, 
			dt_suspensao_programada_p);
		end;
	end if;
	end;
end if;
commit;
ie_apresentar_mensagem_p	:= ie_apresentar_mensagem_w;
end;
$body$
