CREATE OR REPLACE PROCEDURE plt_gerar_w_produto ( nr_prescricao_p bigint, nr_seq_produto_p bigint, nr_seq_adicional_p bigint, ie_acao_p text, nm_usuario_p text, ie_regra_volume_leite_p text, qt_dose_p bigint, ie_via_leite_p text, ds_justificativa_p text) AS $body$
DECLARE


/*
ie_acao_p
D = Deletar
L = Limpar tabela
I = Inlcuir
*/
BEGIN

if (ie_acao_p = 'L') then

	delete	FROM w_produto_lactario
	where	nm_usuario	= nm_usuario_p;

elsif (ie_acao_p = 'D') then

	delete	FROM w_produto_lactario
	where	nm_usuario = nm_usuario_p
	and		nr_seq_produto	= nr_seq_produto_p
	and		nr_prescricao	= nr_prescricao_p;

elsif (ie_acao_p = 'I') then

	insert into w_produto_lactario(
					nr_prescricao,
					nr_seq_produto,
					nr_seq_adicional,
					nm_usuario,
					ie_regra_volume_leite,
					qt_dose,
					ie_via_leite,
					ds_justificativa
				) values (
					nr_prescricao_p,
					nr_seq_produto_p,
					nr_seq_adicional_p,
					nm_usuario_p,
					ie_regra_volume_leite_p,
					qt_dose_p,
					ie_via_leite_p,
					ds_justificativa_p);

end if;

commit;

end;
$body$
