CREATE OR REPLACE PROCEDURE plt_obter_data_estender ( ie_estender_plano_atual_p text, nr_atendimento_P bigint, nr_prescricao_p bigint, cd_estabelecimento_p bigint, cd_perfil_p bigint, cd_pessoa_p text, cd_setor_atendimento_p bigint, dt_quebra_p timestamp, hr_quebra_p timestamp, dt_inicio_estender_p INOUT timestamp, dt_final_estender_p INOUT timestamp, nm_usuario_p text) AS $body$
DECLARE

 
dt_inicio_estender_w	timestamp;
dt_final_estender_w	timestamp;


BEGIN 
if (ie_estender_plano_atual_p IS NOT NULL AND ie_estender_plano_atual_p::text <> '') and (nr_atendimento_P IS NOT NULL AND nr_atendimento_P::text <> '') and (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (cd_estabelecimento_p IS NOT NULL AND cd_estabelecimento_p::text <> '') and (cd_perfil_p IS NOT NULL AND cd_perfil_p::text <> '') and (cd_pessoa_p IS NOT NULL AND cd_pessoa_p::text <> '') and 
	--(cd_setor_atendimento_p is not null) and 
	(dt_quebra_p IS NOT NULL AND dt_quebra_p::text <> '') and (hr_quebra_p IS NOT NULL AND hr_quebra_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then 
	begin 
	 
	if ('S' = ie_estender_plano_atual_p) then 
		begin 
		 
		dt_inicio_estender_w	:= converte_char_data(to_char(clock_timestamp(),'dd/mm/yyyy'),to_char(plt_obter_prim_hor_plano(nr_atendimento_p, cd_setor_atendimento_p, clock_timestamp(), nm_usuario_p),'hh24:mi')||':00',null);
 
		end;
	else	 
		begin 
		select	plt_obter_inicio_extensao(cd_pessoa_p, nm_usuario_p, cd_setor_atendimento_p, dt_quebra_p, hr_quebra_p, cd_perfil_p,nr_atendimento_P) 
		into STRICT	dt_inicio_estender_w 
		;
		end;
	end if;
 
	if (dt_inicio_estender_w IS NOT NULL AND dt_inicio_estender_w::text <> '') then 
		begin 
		 
		select	plt_obter_final_extensao(to_char(dt_inicio_estender_w, 'dd/mm/yyyy'), to_char(to_date(dt_inicio_estender_w,'dd/mm/yyyy hh24:mi:ss'),'hh24:mi:ss'), nr_prescricao_p ,nr_atendimento_P,cd_estabelecimento_p,cd_perfil_p,nm_usuario_p,cd_setor_atendimento_p) 
		into STRICT	dt_final_estender_w 
		from	prescr_medica 
		where	nr_prescricao = nr_prescricao_p;
	 
		end;	
	end if;
	end;
	 
	dt_inicio_estender_p	:= dt_inicio_estender_w;
	dt_final_estender_p	:= dt_final_estender_w;
	 
end if;
 
commit;
 
end;
$body$
