	CASE WHEN coalesce(ie_lib_pend_rep::text, '') = '' THEN  CASE WHEN ie_acm_sn='S' THEN  CASE WHEN obter_se_agrupar_acm_sn_adep(nr_prescricao,nr_seq_gasoterapia,ie_agrupar_acm_sn_p)='N' THEN  nr_seq_gasoterapia  ELSE null END   ELSE nr_seq_gasoterapia END   ELSE nr_seq_gasoterapia END ,
