		CASE WHEN coalesce(a.dt_liberacao::text, '') = '' THEN substr(plt_obter_lib_pend_prescr(a.dt_liberacao_medico,a.dt_liberacao,a.dt_liberacao_farmacia),1,1)  ELSE null END  ie_lib_pend_rep,
