	nr_seq_gas,
	ds_gas,
	ie_acm_sn,
	cd_intervalo,
	qt_gasoterapia,
	ds_prescricao,
	ds_config_gas,
	ie_lib_pend_rep,
	ie_status_item,
	ds_intervalo,
	ie_liberado;


