CREATE OR REPLACE PROCEDURE plt_obter_horarios_assoc_glic ( cd_estabelecimento_p bigint, cd_setor_usuario_p bigint, cd_perfil_p bigint, nm_usuario_p text, nr_atendimento_p bigint, dt_inicial_horarios_p timestamp, dt_final_horarios_p timestamp, dt_validade_limite_p timestamp, ie_prescr_usuario_p text) AS $body$
DECLARE

		dt_horario_w	timestamp;

c01 CURSOR FOR
SELECT	c.dt_horario
from	proc_interno w,
	prescr_procedimento y,
	prescr_material x,
	prescr_mat_hor c,
	prescr_medica a
where	w.nr_sequencia = y.nr_seq_proc_interno
and	y.nr_prescricao = x.nr_prescricao
and	y.nr_sequencia = x.nr_sequencia_proc
and	y.nr_prescricao = a.nr_prescricao
and	x.nr_prescricao = c.nr_prescricao
and	x.nr_sequencia = c.nr_seq_material
and	x.nr_prescricao = a.nr_prescricao
and	c.nr_prescricao = a.nr_prescricao
and	a.nr_atendimento = nr_atendimento_p
and	a.dt_validade_prescr > dt_validade_limite_p
and	w.ie_tipo <> 'BS'
and	w.ie_ivc <> 'S'
and	coalesce(w.ie_ctrl_glic,'NC') = 'CCG'
and	(y.nr_seq_proc_interno IS NOT NULL AND y.nr_seq_proc_interno::text <> '')
and	(y.nr_seq_prot_glic IS NOT NULL AND y.nr_seq_prot_glic::text <> '')
and	coalesce(y.nr_seq_exame::text, '') = ''
and	coalesce(y.nr_seq_solic_sangue::text, '') = ''
and	coalesce(x.nr_seq_kit::text, '') = ''
and	coalesce(y.nr_seq_derivado::text, '') = ''
and	coalesce(y.nr_seq_exame_sangue::text, '') = ''
and	x.ie_agrupador = 5
and	coalesce(c.ie_situacao,'A') = 'A'
and	c.ie_agrupador = 5
and	c.dt_horario between dt_inicial_horarios_p and dt_final_horarios_p
and	((coalesce(c.ie_horario_especial,'N') = 'N') or (c.dt_fim_horario IS NOT NULL AND c.dt_fim_horario::text <> ''))
and	coalesce(a.ie_adep,'S') = 'S'
and	((a.dt_liberacao_medico IS NOT NULL AND a.dt_liberacao_medico::text <> '' AND ie_prescr_usuario_p = 'N') or (a.nm_usuario_original = nm_usuario_p))
group by
	c.dt_horario;


BEGIN

open c01;
loop
fetch c01 into
	dt_horario_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	insert into w_rep_horarios_t(
		nm_usuario,
		dt_horario)
	values (
		nm_usuario_p,
		dt_horario_w);
	end;
end loop;
close c01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
