SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE plt_obter_horarios_solucao ( cd_estabelecimento_p bigint, cd_setor_usuario_p bigint, cd_perfil_p bigint, nm_usuario_p text, nr_atendimento_p bigint, dt_inicial_horarios_p timestamp, dt_final_horarios_p timestamp, dt_validade_limite_p timestamp, ie_formato_solucoes_p text, ie_prescr_usuario_p text) AS $body$
DECLARE


dt_horario_w	timestamp;

c01 CURSOR FOR
SELECT	x.dt_status
from	prescr_solucao x,
	prescr_medica a
where	x.nr_prescricao = a.nr_prescricao
and	a.nr_atendimento = nr_atendimento_p
and	a.dt_validade_prescr > dt_validade_limite_p
and	coalesce(x.nr_seq_dialise::text, '') = ''
and	x.ie_status in ('P','N')
and	x.dt_status between dt_inicial_horarios_p and dt_final_horarios_p
and	coalesce(a.ie_adep,'S') = 'S'
and	obter_se_prescr_vig_adep(a.dt_inicio_prescr,a.dt_validade_prescr,dt_inicial_horarios_p,dt_final_horarios_p) = 'S'
and	((a.dt_liberacao_medico IS NOT NULL AND a.dt_liberacao_medico::text <> '' AND ie_prescr_usuario_p = 'N') or (a.nm_usuario_original = nm_usuario_p))
group by
	x.dt_status;

c02 CURSOR FOR
SELECT	c.dt_horario
from	prescr_solucao x,
	prescr_mat_hor c,
	prescr_medica a
where	x.nr_prescricao = c.nr_prescricao
and	x.nr_seq_solucao = c.nr_seq_solucao
and	x.nr_prescricao = a.nr_prescricao
and	c.nr_prescricao = a.nr_prescricao
and	a.nr_atendimento = nr_atendimento_p
--and	a.dt_validade_prescr > dt_validade_limite_p
and	coalesce(x.nr_seq_dialise::text, '') = ''
and	coalesce(c.ie_situacao,'A') = 'A'
and	c.ie_agrupador = 4
and	coalesce(a.ie_adep,'S') = 'S'
--and	c.dt_horario between dt_inicial_horarios_p and dt_final_horarios_p
and	adep_obter_se_gerar_sol_hor(c.dt_horario, c.dt_inicio_horario, c.dt_fim_horario, c.dt_suspensao, dt_inicial_horarios_p, dt_final_horarios_p) = 'S'
and	obter_se_prescr_vig_adep(a.dt_inicio_prescr,a.dt_validade_prescr,dt_inicial_horarios_p,dt_final_horarios_p) = 'S'
--and	((nvl(c.ie_horario_especial,'N') = 'N') or (c.dt_fim_horario is not null))
and	((a.dt_liberacao_medico IS NOT NULL AND a.dt_liberacao_medico::text <> '' AND ie_prescr_usuario_p = 'N') or (a.nm_usuario_original = nm_usuario_p))
group by
	c.dt_horario;


BEGIN

if (ie_formato_solucoes_p = 'I') then
	begin
	open c01;
	loop
	fetch c01 into dt_horario_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		insert into w_rep_horarios_t(
			nm_usuario,
			dt_horario)
		values (
			nm_usuario_p,
			dt_horario_w);
		end;
	end loop;
	close c01;
	end;
elsif (ie_formato_solucoes_p = 'H') then
	begin
	open c02;
	loop
	fetch c02 into dt_horario_w;
	EXIT WHEN NOT FOUND; /* apply on c02 */
		begin
		insert into w_rep_horarios_t(
			nm_usuario,
			dt_horario)
		values (
			nm_usuario_p,
			dt_horario_w);
		end;
	end loop;
	close c02;
	end;
end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
