CREATE OR REPLACE PROCEDURE plt_obter_npt_adulta ( nm_usuario_p text, nr_atendimento_p bigint, dt_inicial_horarios_p timestamp, dt_final_horarios_p timestamp, dt_validade_limite_p timestamp, ie_prescr_usuario_p text, nr_seq_regra_p bigint) AS $body$
DECLARE


nr_seq_wadep_w			bigint;
nr_prescricao_w			bigint;
nr_seq_npt_w			bigint;
ds_npt_w			varchar(255);
ie_status_solucao_w		varchar(3);
ie_lib_pend_rep_w		varchar(1);
ie_liberado_w			varchar(1);
ie_copiar_w			varchar(1);
ds_cor_titulo_w		varchar(20);

c01 CURSOR FOR
SELECT	nr_prescricao,
	nr_seq_npt,
	ds_npt,
	ie_status_solucao,
	ie_lib_pend_rep,
	ie_liberado
from	(
	SELECT	a.nr_prescricao,
		x.nr_sequencia nr_seq_npt,
		CASE WHEN x.ie_forma='P' THEN  obter_desc_expressao(718006)|| y.ds_npt  ELSE obter_valor_dominio(1988,x.ie_forma) END  ds_npt,
		substr(obter_status_solucao_prescr(6,a.nr_prescricao,x.nr_sequencia),1,3) ie_status_solucao,
		substr(plt_obter_lib_pend_prescr(a.dt_liberacao_medico,a.dt_liberacao,a.dt_liberacao_farmacia),1,1) ie_lib_pend_rep,
		CASE WHEN coalesce(coalesce(a.dt_liberacao,a.dt_liberacao_medico),a.dt_liberacao_farmacia) IS NULL THEN 'N'  ELSE 'S' END  ie_liberado
	FROM prescr_medica a, nut_pac x
LEFT OUTER JOIN protocolo_npt y ON (x.nr_seq_protocolo = y.nr_sequencia)
WHERE x.nr_prescricao = a.nr_prescricao and a.nr_atendimento = nr_atendimento_p and a.dt_validade_prescr > dt_validade_limite_p and x.ie_npt_adulta = 'S' and coalesce(a.ie_adep,'S') = 'S' and ((x.ie_status in ('I','INT')) or
		 ((obter_se_acm_sn(null,null) = 'N') and (x.dt_status between dt_inicial_horarios_p and dt_final_horarios_p)) or
		 ((obter_se_acm_sn(null,null) = 'S') and (obter_se_prescr_vig_adep(a.dt_inicio_prescr,a.dt_validade_prescr,dt_inicial_horarios_p,dt_final_horarios_p) = 'S'))) and ((a.dt_liberacao_medico IS NOT NULL AND a.dt_liberacao_medico::text <> '' AND ie_prescr_usuario_p = 'N') or (a.nm_usuario_original = nm_usuario_p)) group by
		a.nr_prescricao,
		x.nr_sequencia,
		x.ie_forma,
		y.ds_npt,
		a.dt_liberacao_medico,
		a.dt_liberacao,
		a.dt_liberacao_farmacia
	) alias24
group by
	nr_prescricao,
	nr_seq_npt,
	ds_npt,
	ie_status_solucao,
	ie_lib_pend_rep,
	ie_liberado;


BEGIN

ie_copiar_w 	:= plt_obter_se_item_marcado('NAN', nr_seq_regra_p);
ds_cor_titulo_w	:= plt_obter_se_item_marcado('NAN', nr_seq_regra_p);

open c01;
loop
fetch c01 into	nr_prescricao_w,
		nr_seq_npt_w,
		ds_npt_w,
		ie_status_solucao_w,
		ie_lib_pend_rep_w,
		ie_liberado_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	select	nextval('w_rep_t_seq')
	into STRICT	nr_seq_wadep_w
	;

	insert into w_rep_t(
		nr_sequencia,
		nm_usuario,
		ie_tipo_item,
		nr_prescricao,
		nr_seq_item,
		cd_item,
		ds_item,
		ie_acm_sn,
		ie_diferenciado,
		nr_agrupamento,
		nr_seq_proc_interno,
		ie_status_item,
		ie_pendente_liberacao,
		nr_prescricoes,
		ie_liberado,
		ie_copiar,
		ds_cor_titulo)
	values (
		nr_seq_wadep_w,
		nm_usuario_p,
		'NAN',
		nr_prescricao_w,
		nr_seq_npt_w,
		nr_seq_npt_w,
		ds_npt_w,
		'N',
		'N',
		0,
		0,
		ie_status_solucao_w,
		ie_lib_pend_rep_w,
		nr_prescricao_w,
		ie_liberado_w,
		ie_copiar_w,
		ds_cor_titulo_w);
	end;
end loop;
close c01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
