CREATE OR REPLACE PROCEDURE plt_obter_ordem_med ( nm_usuario_p text, nr_atendimento_p bigint, dt_inicial_horarios_p timestamp, dt_final_horarios_p timestamp, dt_validade_limite_p timestamp, ie_prescr_usuario_p text, nr_seq_regra_p bigint) AS $body$
DECLARE


nr_seq_wadep_w			bigint;
nr_prescricao_w			bigint;
nr_seq_item_ordem_w		integer;
nr_seq_ordem_w			bigint;
nr_seq_horario_w		bigint;
ds_ordem_w			varchar(255);
ie_acm_sn_w			varchar(1);
ds_prescricao_w			varchar(255);
ds_diluicao_w			varchar(2000);
ie_lib_pend_rep_w		varchar(1);
ie_liberado_w			varchar(1);
ie_copiar_w			varchar(1);
ds_cor_titulo_w		varchar(20);

c01 CURSOR FOR
SELECT	nr_prescricao,
	nr_seq_item_ordem,
	nr_seq_ordem,
	nr_seq_horario,
	ds_ordem,
	ie_acm_sn,
	ds_prescricao,
	ds_diluicao,
	ie_lib_pend_rep,
	ie_liberado
from	(
	SELECT	a.nr_prescricao,
		b.nr_seq_solucao nr_seq_item_ordem,
		b.nr_sequencia nr_seq_ordem,
		c.nr_sequencia nr_seq_horario,
		x.ds_ordem,
		'N' ie_acm_sn,
		obter_desc_solucao_adep(a.nr_prescricao,b.nr_seq_solucao) ds_prescricao,
		b.ds_ordem ds_diluicao,
		substr(plt_obter_lib_pend_prescr(a.dt_liberacao_medico,a.dt_liberacao,a.dt_liberacao_farmacia),1,1) ie_lib_pend_rep,
		CASE WHEN coalesce(coalesce(a.dt_liberacao,a.dt_liberacao_medico),a.dt_liberacao_farmacia) IS NULL THEN 'N'  ELSE 'S' END  ie_liberado
	from	ordem_medica x,
		prescr_ordem_hor c,
		prescr_medica_ordem b,
		prescr_medica a
	where	x.nr_sequencia = b.nr_seq_ordem
	and	c.nr_seq_ordem = b.nr_sequencia
	and	b.nr_prescricao = a.nr_prescricao
	and	a.nr_atendimento = nr_atendimento_p
	and	a.dt_validade_prescr > dt_validade_limite_p
	and	(b.nr_seq_solucao IS NOT NULL AND b.nr_seq_solucao::text <> '')
	and	coalesce(a.ie_adep,'S') = 'S'
	and	(('N' = 'N' AND c.dt_horario between dt_inicial_horarios_p and dt_final_horarios_p) or
		 (('N' = 'S') and (obter_se_prescr_vig_adep(a.dt_inicio_prescr,a.dt_validade_prescr,dt_inicial_horarios_p,dt_final_horarios_p) = 'S')))
	and	((a.dt_liberacao_medico IS NOT NULL AND a.dt_liberacao_medico::text <> '' AND ie_prescr_usuario_p = 'N') or (a.nm_usuario_original = nm_usuario_p))
	group by
		a.nr_prescricao,
		b.nr_seq_solucao,
		b.nr_sequencia,
		c.nr_sequencia,
		x.ds_ordem,
		'N',
		b.ds_ordem,
		a.dt_liberacao_medico,
		a.dt_liberacao,
		a.dt_liberacao_farmacia
	
union all

	select	a.nr_prescricao,
		b.nr_seq_material nr_seq_item_ordem,
		b.nr_sequencia nr_seq_ordem,
		c.nr_sequencia nr_seq_horario,
		x.ds_ordem,
		'N' ie_acm_sn,
		z.ds_material ds_prescricao,
		b.ds_ordem ds_diluicao,
		substr(plt_obter_lib_pend_prescr(a.dt_liberacao_medico,a.dt_liberacao,a.dt_liberacao_farmacia),1,1) ie_lib_pend_rep,
		CASE WHEN coalesce(coalesce(a.dt_liberacao,a.dt_liberacao_medico),a.dt_liberacao_farmacia) IS NULL THEN 'N'  ELSE 'S' END  ie_liberado
	from	material z,
		prescr_material y,
		ordem_medica x,
		prescr_ordem_hor c,
		prescr_medica_ordem b,
		prescr_medica a
	where	z.cd_material = y.cd_material
	and	y.nr_prescricao = a.nr_prescricao
	and	y.nr_prescricao = b.nr_prescricao
	and	y.nr_sequencia = b.nr_seq_material
	and	x.nr_sequencia = b.nr_seq_ordem
	and	c.nr_seq_ordem = b.nr_sequencia
	and	b.nr_prescricao = a.nr_prescricao
	and	a.nr_atendimento = nr_atendimento_p
	and	a.dt_validade_prescr > dt_validade_limite_p
	and	(b.nr_seq_material IS NOT NULL AND b.nr_seq_material::text <> '')
	and	coalesce(a.ie_adep,'S') = 'S'
	and	(('N' = 'N' AND c.dt_horario between dt_inicial_horarios_p and dt_final_horarios_p) or
		 (('N' = 'S') and (obter_se_prescr_vig_adep(a.dt_inicio_prescr,a.dt_validade_prescr,dt_inicial_horarios_p,dt_final_horarios_p) = 'S')))
	and	((a.dt_liberacao_medico IS NOT NULL AND a.dt_liberacao_medico::text <> '' AND ie_prescr_usuario_p = 'N') or (a.nm_usuario_original = nm_usuario_p))
	group by
		a.nr_prescricao,
		b.nr_seq_material,
		b.nr_sequencia,
		c.nr_sequencia,
		x.ds_ordem,
		'N',
		z.ds_material,
		b.ds_ordem,
		a.dt_liberacao_medico,
		a.dt_liberacao,
		a.dt_liberacao_farmacia
	) alias31
group by
	nr_prescricao,
	nr_seq_item_ordem,
	nr_seq_ordem,
	nr_seq_horario,
	ds_ordem,
	ie_acm_sn,
	ds_prescricao,
	ds_diluicao,
	ie_lib_pend_rep,
	ie_liberado;


BEGIN

ie_copiar_w 	:= plt_obter_se_item_marcado('B', nr_seq_regra_p);
ds_cor_titulo_w	:= plt_obter_se_item_marcado('B', nr_seq_regra_p);

open c01;
loop
fetch c01 into	nr_prescricao_w,
		nr_seq_item_ordem_w,
		nr_seq_ordem_w,
		nr_seq_horario_w,
		ds_ordem_w,
		ie_acm_sn_w,
		ds_prescricao_w,
		ds_diluicao_w,
		ie_lib_pend_rep_w,
		ie_liberado_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	select	nextval('w_rep_t_seq')
	into STRICT	nr_seq_wadep_w
	;

	insert into w_rep_t(
		nr_sequencia,
		nm_usuario,
		ie_tipo_item,
		nr_prescricao,
		nr_seq_item,
		cd_item,
		ds_item,
		ie_acm_sn,
		ds_prescricao,
		ds_diluicao,
		cd_intervalo,
		ie_diferenciado,
		nr_dia_util,
		nr_seq_proc_interno,
		ie_medic_controlado,
		nr_agrupamento,
		qt_item,
		ie_pendente_liberacao,
		ie_liberado,
		ie_copiar,
		ds_cor_titulo)
	values (
		nr_seq_wadep_w,
		nm_usuario_p,
		'B',
		nr_prescricao_w,
		nr_seq_item_ordem_w,
		nr_seq_horario_w,
		ds_ordem_w,
		ie_acm_sn_w,
		ds_prescricao_w,
		ds_diluicao_w,
		'X',
		'N',
		0,
		0,
		'N',
		0,
		0,
		ie_lib_pend_rep_w,
		ie_liberado_w,
		ie_copiar_w,
		ds_cor_titulo_w);
	end;
end loop;
close c01;
end;
$body$
