CREATE OR REPLACE PROCEDURE plt_obter_sne ( nm_usuario_p text, nr_atendimento_p bigint, dt_inicial_horarios_p timestamp, dt_final_horarios_p timestamp, dt_validade_limite_p timestamp, ie_prescr_usuario_p text, nr_seq_regra_p bigint) AS $body$
DECLARE


nr_seq_wadep_w			bigint;
nr_prescricao_w			bigint;
nr_seq_material_w		integer;
cd_material_w			integer;
ds_material_w			varchar(255);
ie_acm_sn_w			varchar(1);
cd_intervalo_w			varchar(7);
qt_dose_w			double precision;
nr_agrupamento_w		double precision;
ds_prescricao_w			varchar(100);
ie_status_solucao_w		varchar(3);
ie_status_w			varchar(1);
ie_lib_pend_rep_w		varchar(1);
ie_liberado_w			varchar(1);
cd_unid_med_qtde_w		varchar(30);
ie_via_administracao_w		varchar(5);
ds_interv_prescr_w		varchar(15);
ie_erro_w			integer;
ie_copiar_w			varchar(1);
ds_cor_titulo_w		varchar(20);

c01 CURSOR FOR
SELECT	nr_prescricao,
	nr_seq_material,
	cd_material,
	ds_material,
	ie_acm_sn,
	cd_intervalo,
	qt_dose,
	ds_prescricao,
	ie_status_solucao,
	ie_suspenso,
	cd_unidade_medida_dose,
	ie_via_aplicacao,
	ds_intervalo,
	ie_lib_pend_rep,
	ie_liberado,
	ie_erro
from	(
	SELECT	a.nr_prescricao,
		x.nr_sequencia nr_seq_material,
		x.cd_material,
		y.ds_material,
		obter_se_acm_sn(x.ie_acm,x.ie_se_necessario) ie_acm_sn,
		x.cd_intervalo,
		x.qt_dose,
		substr(adep_obter_um_dosagem_prescr(a.nr_prescricao,x.nr_sequencia,x.ie_acm,x.ie_se_necessario),1,100) ds_prescricao,
		substr(plt_obter_status_solucao(2,a.nr_prescricao,x.nr_sequencia),1,3) ie_status_solucao,
		coalesce(x.ie_suspenso,'N') ie_suspenso,
		x.cd_unidade_medida_dose,
		x.ie_via_aplicacao,
		substr(obter_desc_intervalo_prescr(x.cd_intervalo),1,15) ds_intervalo,
		substr(plt_obter_lib_pend_prescr(a.dt_liberacao_medico,a.dt_liberacao,a.dt_liberacao_farmacia),1,1) ie_lib_pend_rep,
		CASE WHEN coalesce(coalesce(a.dt_liberacao,a.dt_liberacao_medico),a.dt_liberacao_farmacia) IS NULL THEN 'N'  ELSE 'S' END  ie_liberado,
		x.ie_erro
	from	material y,
		prescr_material x,
		prescr_medica a
	where	y.cd_material = x.cd_material
	and	x.nr_prescricao = a.nr_prescricao
	and	a.nr_atendimento = nr_atendimento_p
	and	a.dt_validade_prescr > dt_validade_limite_p
	and	x.ie_agrupador = 8
	and	coalesce(a.ie_adep,'S') = 'S'
	and	((x.ie_status in ('I','INT')) or
		 ((obter_se_acm_sn(x.ie_acm,x.ie_se_necessario) = 'N') and (x.dt_status between dt_inicial_horarios_p and dt_final_horarios_p)) or
		 ((obter_se_acm_sn(x.ie_acm,x.ie_se_necessario) = 'S') and (obter_se_prescr_vig_adep(a.dt_inicio_prescr,a.dt_validade_prescr,dt_inicial_horarios_p,dt_final_horarios_p) = 'S')))
	and	((a.dt_liberacao_medico IS NOT NULL AND a.dt_liberacao_medico::text <> '' AND ie_prescr_usuario_p = 'N') or (a.nm_usuario_original = nm_usuario_p))
	group by
		a.nr_prescricao,
		x.nr_sequencia,
		x.cd_material,
		y.ds_material,
		x.ie_acm,
		x.ie_se_necessario,
		x.cd_intervalo,
		x.qt_dose,
		x.ie_suspenso,
		x.cd_unidade_medida_dose,
		x.ie_via_aplicacao,
		a.dt_liberacao_medico,
		a.dt_liberacao,
		a.dt_liberacao_farmacia,
		x.ie_erro
	) alias28
group by
	nr_prescricao,
	nr_seq_material,
	cd_material,
	ds_material,
	ie_acm_sn,
	cd_intervalo,
	qt_dose,
	ds_prescricao,
	ie_status_solucao,
	ie_suspenso,
	cd_unidade_medida_dose,
	ie_via_aplicacao,
	ds_intervalo,
	ie_lib_pend_rep,
	ie_liberado,
	ie_erro;


BEGIN

ie_copiar_w 	:= plt_obter_se_item_marcado('SNE', nr_seq_regra_p);
ds_cor_titulo_w	:= plt_obter_se_item_marcado('SNE', nr_seq_regra_p);

open c01;
loop
fetch c01 into	nr_prescricao_w,
		nr_seq_material_w,
		cd_material_w,
		ds_material_w,
		ie_acm_sn_w,
		cd_intervalo_w,
		qt_dose_w,
		ds_prescricao_w,
		ie_status_solucao_w,
		ie_status_w,
		cd_unid_med_qtde_w,
		ie_via_administracao_w,
		ds_interv_prescr_w,
		ie_lib_pend_rep_w,
		ie_liberado_w,
		ie_erro_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	select	nextval('w_rep_t_seq')
	into STRICT	nr_seq_wadep_w
	;

	insert into w_rep_t(
		nr_sequencia,
		nm_usuario,
		ie_tipo_item,
		nr_prescricao,
		nr_seq_item,
		cd_item,
		ds_item,
		ie_acm_sn,
		cd_intervalo,
		qt_item,
		nr_agrupamento,
		ds_prescricao,
		ie_status_item,
		nr_seq_proc_interno,
		ie_diferenciado,
		cd_unid_med_qtde,
		ie_via_aplicacao,
		ds_interv_prescr,
		ie_pendente_liberacao,
		ie_liberado,
		ie_erro,
		ie_copiar,
		ds_cor_titulo)
	values (
		nr_seq_wadep_w,
		nm_usuario_p,
		'SNE',
		nr_prescricao_w,
		nr_seq_material_w,
		cd_material_w,
		ds_material_w,
		ie_acm_sn_w,
		cd_intervalo_w,
		qt_dose_w,
		0,
		ds_prescricao_w,
		ie_status_solucao_w,
		0,
		'N',
		cd_unid_med_qtde_w,
		ie_via_administracao_w,
		ds_interv_prescr_w,
		ie_lib_pend_rep_w,
		ie_liberado_w,
		ie_erro_w,
		ie_copiar_w,
		ds_cor_titulo_w);
	end;
end loop;
close c01;

end;
$body$
