CREATE OR REPLACE PROCEDURE plt_sincronizar_jejum ( nm_usuario_p text, nr_atendimento_p bigint, dt_validade_limite_p timestamp, dt_horario_p timestamp, nr_horario_p integer, ie_prescr_usuario_p text) AS $body$
DECLARE


ds_sep_bv_w			varchar(50);
nr_prescricao_w			bigint;
cd_jejum_w			varchar(255);
ds_jejum_w			varchar(255);
ds_vigencia_w			varchar(255);
ds_comando_update_w		varchar(4000);
ie_lib_pend_rep_w		varchar(1);

c01 CURSOR FOR
SELECT	a.nr_prescricao,
	to_char(c.dt_inicio,'dd/mm/yyyy hh24:mi:ss') || '-' || to_char(c.dt_fim,'dd/mm/yyyy hh24:mi:ss') cd_jejum,
	x.ds_tipo ds_jejum,
	obter_desc_expressao(301700) ||': ' || to_char(c.dt_inicio,'dd/mm/yyyy hh24:mi:ss') || obter_desc_expressao(345996) || to_char(c.dt_fim,'dd/mm/yyyy hh24:mi:ss') ds_vigencia,
	substr(plt_obter_lib_pend_prescr(a.dt_liberacao_medico,a.dt_liberacao,a.dt_liberacao_farmacia),1,1)
from	rep_tipo_jejum x,
	rep_jejum c,
	prescr_medica a
where	x.nr_sequencia = c.nr_seq_tipo
and	c.nr_prescricao = a.nr_prescricao
and	a.nr_atendimento = nr_atendimento_p
and	a.dt_validade_prescr > dt_validade_limite_p
and	c.dt_inicio = dt_horario_p
and	coalesce(a.ie_adep,'S') = 'S'
and	((a.dt_liberacao_medico IS NOT NULL AND a.dt_liberacao_medico::text <> '' AND ie_prescr_usuario_p = 'N') or (a.nm_usuario_original = nm_usuario_p))
group by
	a.nr_prescricao,
	c.dt_inicio,
	c.dt_fim,
	x.ds_tipo,
	a.dt_liberacao_medico,
	a.dt_liberacao,
	a.dt_liberacao_farmacia;


BEGIN
ds_sep_bv_w := obter_separador_bv;
open c01;
loop
fetch c01 into	nr_prescricao_w,
		cd_jejum_w,
		ds_jejum_w,
		ds_vigencia_w,
		ie_lib_pend_rep_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	ds_comando_update_w	:=	' update w_rep_t ' ||
					' set nr_prescricoes = adep_juntar_prescricao(nr_prescricoes,:nr_prescricao) ' ||
					' where nm_usuario = :nm_usuario ' ||
					' and ie_tipo_item = :ie_tipo ' ||
					' and ie_pendente_liberacao = :ie_pendente_liberacao ' ||
					' and cd_item = :cd_item ' ||
					' and ((ds_prescricao = :ds_vigencia) or (ds_prescricao is null)) ';

	CALL exec_sql_dinamico_bv('PLT', ds_comando_update_w,	'nr_prescricao=' || to_char(nr_prescricao_w) || ds_sep_bv_w ||
								'nm_usuario=' || nm_usuario_p || ds_sep_bv_w ||
								'ie_tipo=J' || ds_sep_bv_w ||
								'ie_pendente_liberacao=' || ie_lib_pend_rep_w || ds_sep_bv_w ||
								'cd_item=' || cd_jejum_w || ds_sep_bv_w ||
								'ds_vigencia=' || ds_vigencia_w || ds_sep_bv_w );
	end;
end loop;
close c01;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
