CREATE OR REPLACE PROCEDURE plt_verifica_medic_dupl_dia ( cd_material_p bigint, nr_prescricao_p bigint, nm_usuario_p text, ds_retorno_p INOUT text) AS $body$
DECLARE

cd_pessoa_fisica_w	varchar(10);
dt_prescricao_w		timestamp;
dt_suspensao_w		timestamp;
nm_medico_w		varchar(60);
ie_suspenso_w		varchar(10);
ie_consitir_susp_w	varchar(10);
ie_consistir_setor_w	varchar(1);
cd_estabelecimento_w	bigint;
cd_setor_atendimento_w	bigint;
dt_prescricao_ww	timestamp;
dt_inicio_prescr_w	timestamp;
dt_validade_prescr_w	timestamp;

C01 CURSOR FOR 
SELECT	substr(obter_nome_medico(b.cd_medico,'N'),1,60), 
	CASE WHEN a.ie_suspenso='S' THEN 'SUSPENSO'  ELSE '' END , 
	a.dt_suspensao, 
	b.dt_prescricao 
from	material		c, 
	prescr_material		a, 
	prescr_medica		b 
where	b.nr_prescricao		<> nr_prescricao_p 
and	a.cd_material		= cd_material_p 
and	a.cd_material		= c.cd_material 
and	b.cd_pessoa_fisica	= cd_pessoa_fisica_w 
and	obter_se_prescr_vig_adep(b.dt_inicio_prescr, b.dt_validade_prescr, dt_inicio_prescr_w, dt_validade_prescr_w) = 'S' 
and	a.nr_prescricao		= b.nr_prescricao 
and	coalesce(a.ie_modificado,'N') <> 'S' 
and	coalesce(c.ie_consiste_dupl,'S') = 'S' 
and	((ie_consitir_susp_w	= 'S') or (coalesce(a.ie_suspenso,'N') = 'N')) 
and	((ie_consistir_setor_w = 'S') or (b.cd_setor_atendimento = cd_setor_atendimento_w));


BEGIN 
 
ie_consitir_susp_w := Obter_Param_Usuario(924, 307, Obter_Perfil_Ativo, nm_usuario_p, cd_estabelecimento_w, ie_consitir_susp_w);
ie_consistir_setor_w := Obter_Param_Usuario(924, 902, Obter_Perfil_Ativo, nm_usuario_p, cd_estabelecimento_w, ie_consistir_setor_w);
 
select	dt_prescricao, 
	cd_pessoa_fisica, 
	cd_estabelecimento, 
	dt_inicio_prescr, 
	dt_validade_prescr, 
	cd_setor_atendimento 
into STRICT	dt_prescricao_w, 
	cd_pessoa_fisica_w, 
	cd_estabelecimento_w, 
	dt_inicio_prescr_w, 
	dt_validade_prescr_w, 
	cd_setor_atendimento_w 
from	prescr_medica 
where	nr_prescricao = nr_prescricao_p;
 
open	C01;
loop 
fetch	C01 into 
	nm_medico_w, 
	ie_suspenso_w, 
	dt_suspensao_w, 
	dt_prescricao_ww;
EXIT WHEN NOT FOUND; /* apply on C01 */
	if (dt_suspensao_w IS NOT NULL AND dt_suspensao_w::text <> '') then 
		ds_retorno_p	:= substr(ds_retorno_p || '  ' || ie_suspenso_w || chr(13)|| obter_desc_expressao(622367) || nm_medico_w || chr(13) || obter_desc_expressao(645254)/*'  Dt. susp.: '*/ ||': '|| to_char(dt_suspensao_w,'dd/mm/yyyy hh24:mi')||chr(13)||chr(13),1,255);
	else 
		ds_retorno_p	:= substr(ds_retorno_p || obter_desc_expressao(622367) || nm_medico_w || chr(13)||chr(13),1,255);
	end if;
end loop;
close C01;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
