CREATE OR REPLACE PROCEDURE pmo_aprovar_ideia ( nr_seq_pmo_proj_ideias_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_pessoa_fisica_w 	pmo_proj_ideias.cd_pessoa_fisica%type;
nr_seq_ordem_w		pmo_proj_ideias.nr_seq_ordem%type;
nr_seq_program_w	pmo_proj_ideias.nr_seq_programa%type;


BEGIN

	select	cd_pessoa_fisica,
		nr_seq_ordem,
		nr_seq_programa
	into STRICT	cd_pessoa_fisica_w,
		nr_seq_ordem_w,
		nr_seq_program_w
	from	pmo_proj_ideias
	where	nr_sequencia = nr_seq_pmo_proj_ideias_p;

	update	pmo_proj_ideias
	set	ie_situacao 		= 'A',
		nm_usuario 		= nm_usuario_p,
		dt_atualizacao 		= clock_timestamp(),
		dt_aprovacao		= clock_timestamp()
	where	nr_sequencia 		= nr_seq_pmo_proj_ideias_p;
	
	
	insert into arm_project(nr_sequencia,
				nm_usuario,
				dt_atualizacao,
				cd_ideia_requester,
				nr_seq_ordem,
				nr_seq_program
				)
	values (	nextval('arm_project_seq'),
				nm_usuario_p,
				clock_timestamp(),
				cd_pessoa_fisica_w,
				nr_seq_ordem_w,
				nr_seq_program_w
			);

	commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
