CREATE OR REPLACE PROCEDURE pmo_gerar_escore_projetos (nr_seq_regra_escore_p bigint, dt_escore_p timestamp, nm_usuario_p text) AS $body$
DECLARE

 
nr_seq_escore_w		bigint;
nr_seq_projeto_w	bigint;
dt_escore_w		timestamp;

c01 CURSOR FOR 
	SELECT	a.nr_sequencia 
	from 	proj_projeto a 
	where 	a.ie_origem = 'D' 
	and 	a.dt_inicio_real >= to_date('01/01/2013','dd/mm/yyyy') 
	and 	a.IE_STATUS = 'T';	
 

BEGIN 
 
dt_escore_w	:= trunc(dt_escore_p, 'month');
 
open	c01;
loop 
fetch C01 into	 
	nr_seq_projeto_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
 
	select	coalesce(max(nr_sequencia),0) 
	into STRICT	nr_seq_escore_w 
	from	proj_escore 
	where	nr_seq_proj	= nr_seq_projeto_w 
	and	dt_escore between dt_escore_w and fim_mes(dt_escore_w);
 
	if (nr_seq_escore_w = 0) then 
		begin 
 
		select	nextval('proj_escore_seq') 
		into STRICT	nr_seq_escore_w 
		;
 
 
		insert	into proj_escore(NR_SEQUENCIA, 
			NR_SEQ_PROJ, 
			DT_ATUALIZACAO, 
			NM_USUARIO, 
			DT_ATUALIZACAO_NREC, 
			NM_USUARIO_NREC, 
			DT_ESCORE, 
			QT_ESCORE, 
			NR_SEQ_REGRA, 
			DT_LIBERACAO, 
			NM_USUARIO_LIB, 
			NR_SEQ_GERENCIA) 
		values (nr_seq_escore_w, 
			nr_seq_projeto_w, 
			clock_timestamp(), 
			nm_usuario_p, 
			clock_timestamp(), 
			nm_usuario_p, 
			dt_escore_w, 
			0, 
			nr_seq_regra_escore_p, 
			clock_timestamp(), 
			nm_usuario_p, 
			null);		
 
		CALL proj_gerar_itens_escore(nr_seq_escore_w, nm_usuario_p);
 
		end;
	end if;
 
	if (nr_seq_escore_w > 0) then 
		CALL proj_gerar_calculo_escore(nr_seq_escore_w, nm_usuario_p);
 
	end if;
	end;
end loop;
close C01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
