SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ppm_metrica_backlog ( nr_seq_metrica_p bigint, nr_seq_objetivo_metrica_p bigint, cd_pessoa_fisica_p text, nm_usuario_p text, dt_referencia_p timestamp) AS $body$
DECLARE

			
dt_ref_inicio_w 	timestamp;
dt_ref_fim_w 		timestamp;
qt_backlog_w		bigint;
dt_referencia_w		timestamp;
nr_seq_diretoria_w	ppm_objetivo.nr_seq_diretoria%type;
nr_seq_gerencia_w	ppm_objetivo.nr_seq_gerencia%type;
nr_seq_grupo_w		ppm_objetivo.nr_seq_grupo%type;


BEGIN

dt_ref_inicio_w := pkg_date_utils.start_of(dt_referencia_p,'MONTH');
dt_ref_fim_w 	:= pkg_date_utils.end_of(last_day(dt_referencia_p),'DAY');
dt_referencia_w	:= trunc(dt_referencia_p);

select	max(nr_seq_gerencia),
	max(nr_seq_grupo),
	max(nr_seq_diretoria)
into STRICT	nr_seq_gerencia_w,
	nr_seq_grupo_w,
	nr_seq_diretoria_w
from	ppm_objetivo_metrica a,
	ppm_objetivo_meta b,
	ppm_objetivo c
where	a.nr_sequencia		= nr_seq_objetivo_metrica_p
and	a.nr_seq_meta		= b.nr_sequencia
and	b.nr_seq_objetivo	= c.nr_sequencia;

qt_backlog_w	:= 0;

if (nr_seq_grupo_w IS NOT NULL AND nr_seq_grupo_w::text <> '') then

	begin
	select	coalesce(a.QT_BACKLOG_CLIENTE,0)
	into STRICT	qt_backlog_w
	from	w_indicador_desenv_apres a
	where	a.nr_seq_grupo	= nr_seq_grupo_w
	and	trunc(a.dt_referencia,'month') = trunc(dt_referencia_w,'month')
	and	a.dt_referencia =
		(SELECT	max(dt_referencia)
		from	w_indicador_desenv_apres x
		where	x.nr_seq_grupo		= a.nr_seq_grupo
		and	x.ie_abrangencia 	= 'GRU'
		and	trunc(x.dt_referencia,'month') = trunc(dt_referencia_w,'month'))
	and	a.ie_abrangencia 	= 'GRU';
	exception
	when others then
		qt_backlog_w := 0;
	end;
	

	CALL PPM_GRAVAR_RESULTADO(nr_seq_objetivo_metrica_p,dt_referencia_p,qt_backlog_w, 0, 0, nm_usuario_p);
	
elsif (nr_seq_gerencia_w IS NOT NULL AND nr_seq_gerencia_w::text <> '') then

	begin
	select	coalesce(a.QT_BACKLOG_CLIENTE,0)
	into STRICT	qt_backlog_w
	from	w_indicador_desenv_apres a
	where	a.nr_seq_gerencia	= nr_seq_gerencia_w
	and	trunc(a.dt_referencia,'month') = trunc(dt_referencia_w,'month')
	and	a.dt_referencia =
		(SELECT	max(dt_referencia)
		from	w_indicador_desenv_apres x
		where	x.nr_seq_gerencia	= a.nr_seq_gerencia
		and	x.ie_abrangencia 	= 'GER'
		and	trunc(x.dt_referencia,'month') = trunc(dt_referencia_w,'month'))
	and	a.ie_abrangencia 	= 'GER';
	exception
	when others then
		qt_backlog_w := 0;
	end;
	

	CALL PPM_GRAVAR_RESULTADO(nr_seq_objetivo_metrica_p,dt_referencia_p,qt_backlog_w, 0, 0, nm_usuario_p);
	
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
