SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ppm_metrica_defects_internal ( nr_seq_metrica_p bigint, nr_seq_objetivo_metrica_p bigint, cd_pessoa_fisica_p text, nm_usuario_p text, dt_referencia_p timestamp) AS $body$
DECLARE


dt_ref_inicio_w 	timestamp;
dt_ref_fim_w 		timestamp;
resultado_w			bigint;
qt_defeito_w		bigint;
qt_os_recebida_w	bigint;


BEGIN

dt_ref_inicio_w := pkg_date_utils.start_of(dt_referencia_p,'MONTH');
dt_ref_fim_w 	:= pkg_date_utils.end_of(last_day(dt_referencia_p),'DAY');


select	count(1)
into STRICT	qt_defeito_w
from	gerencia_wheb g,
	man_localizacao l,
	OS_ERRO_GERENCIA_V a
where	a.dt_liberacao between dt_ref_inicio_w and dt_ref_fim_w
and	a.nr_seq_gerencia	= g.nr_sequencia
and	a.nr_seq_localizacao	= l.nr_sequencia
and	a.ie_plataforma		= 'H'
/*and	g.ie_area_gerencia = 'PDES'*/

and (a.cd_pessoa_fisica = cd_pessoa_fisica_p or coalesce(cd_pessoa_fisica_p::text, '') = '');

select	count(1)
into STRICT	qt_os_recebida_w
from	man_localizacao l,
	MAN_ORDEM_SERVICO c
where	c.dt_fim_Real between dt_ref_inicio_w and dt_ref_fim_w
and	c.nr_seq_localizacao	= l.nr_sequencia
and	c.ie_plataforma		= 'H'
/*and	g.ie_area_gerencia = 'PDES'*/

and (exists (SELECT	1
		from	usuario y,
			man_ordem_serv_ativ x
		where	x.nm_usuario_exec = y.nm_usuario
		and	x.nr_seq_ordem_serv = c.nr_sequencia
		and	y.cd_pessoa_fisica = cd_pessoa_fisica_p) or coalesce(cd_pessoa_fisica_p::text, '') = '');


resultado_w	:= dividir(qt_defeito_w * 100,qt_os_recebida_w);


CALL PPM_GRAVAR_RESULTADO(nr_seq_objetivo_metrica_p,dt_referencia_p,resultado_w, qt_os_recebida_w, qt_defeito_w, nm_usuario_p);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE ppm_metrica_defects_internal ( nr_seq_metrica_p bigint, nr_seq_objetivo_metrica_p bigint, cd_pessoa_fisica_p text, nm_usuario_p text, dt_referencia_p timestamp) FROM PUBLIC;

