SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ppm_metrica_os_encerrada ( nr_seq_metrica_p bigint, nr_seq_objetivo_metrica_p bigint, cd_pessoa_fisica_p text, nm_usuario_p text, dt_referencia_p timestamp) AS $body$
DECLARE


dt_ref_inicio_w 	timestamp;
dt_ref_fim_w 		timestamp;
qt_os_encerradas_w	bigint;
qt_acumulado_w		bigint;
nm_usuario_exec_w	usuario.nm_usuario%type;
dt_referencia_w		timestamp;
nr_seq_diretoria_w	ppm_objetivo.nr_seq_diretoria%type;
nr_seq_gerencia_w	ppm_objetivo.nr_seq_gerencia%type;
nr_seq_grupo_w		ppm_objetivo.nr_seq_grupo%type;
dt_fim_ano_w		timestamp;
nr_seq_acumulado_w	bigint;


BEGIN

dt_ref_inicio_w := pkg_date_utils.start_of(dt_referencia_p,'MONTH');
dt_ref_fim_w 	:= pkg_date_utils.end_of(last_day(dt_referencia_p),'DAY');
dt_referencia_w	:= trunc(dt_referencia_p);

select	max(nr_seq_gerencia),
	max(nr_seq_grupo),
	max(nr_seq_diretoria)
into STRICT	nr_seq_gerencia_w,
	nr_seq_grupo_w,
	nr_seq_diretoria_w
from	ppm_objetivo_metrica a,
	ppm_objetivo_meta b,
	ppm_objetivo c
where	a.nr_sequencia		= nr_seq_objetivo_metrica_p
and	a.nr_seq_meta		= b.nr_sequencia
and	b.nr_seq_objetivo	= c.nr_sequencia;

select	max(nm_usuario)
into STRICT	nm_usuario_exec_w
from	usuario x
where	x.cd_pessoa_fisica = cd_pessoa_fisica_p
and	x.ie_situacao	= 'A';

qt_os_encerradas_w	:= 0;

dt_fim_ano_w	:= trunc(PKG_DATE_UTILS.end_of(to_date('31/12/' || to_char(dt_referencia_p,'yyyy'),'dd/mm/yyyy'),'DAY'));

if (nr_seq_grupo_w IS NOT NULL AND nr_seq_grupo_w::text <> '') then

	select	max(coalesce(QT_ENCERRADA_PESSOA,0)),
		max(coalesce(QT_OS_ENCERRADA_PF_ANO,0))
	into STRICT	qt_os_encerradas_w,
		qt_acumulado_w
	from	w_indicador_desenv_apres
	where	dt_referencia 	= dt_referencia_w
	and	nr_seq_grupo	= nr_seq_grupo_w
	and	ie_abrangencia 	= 'GRU';

	CALL PPM_GRAVAR_RESULTADO(nr_seq_objetivo_metrica_p,dt_referencia_p,qt_os_encerradas_w, 0, 0, nm_usuario_p);

	select	max(nr_sequencia)
	into STRICT	nr_seq_acumulado_w
	from	ppm_objetivo_result a
	where	a.nr_seq_metrica	= nr_seq_objetivo_metrica_p
	and	trunc(dt_referencia)	= dt_fim_ano_w;

	update	ppm_objetivo_result
	set	vl_resultado_calc	= qt_acumulado_w
	where	nr_sequencia		= nr_seq_acumulado_w;

elsif (nr_seq_gerencia_w IS NOT NULL AND nr_seq_gerencia_w::text <> '') then

	select	max(coalesce(QT_ENCERRADA_PESSOA,0)),
		max(coalesce(QT_OS_ENCERRADA_PF_ANO,0))
	into STRICT	qt_os_encerradas_w,
		qt_acumulado_w
	from	w_indicador_desenv_apres
	where	dt_referencia 	= dt_referencia_w
	and	nr_seq_gerencia	= nr_seq_gerencia_w
	and	ie_abrangencia 	= 'GER';

	CALL PPM_GRAVAR_RESULTADO(nr_seq_objetivo_metrica_p,dt_referencia_p,qt_os_encerradas_w, 0, 0, nm_usuario_p);

	select	max(nr_sequencia)
	into STRICT	nr_seq_acumulado_w
	from	ppm_objetivo_result a
	where	a.nr_seq_metrica	= nr_seq_objetivo_metrica_p
	and	trunc(dt_referencia)	= dt_fim_ano_w;

	update	ppm_objetivo_result
	set	vl_resultado_calc	= qt_acumulado_w
	where	nr_sequencia		= nr_seq_acumulado_w;

elsif (nr_seq_diretoria_w IS NOT NULL AND nr_seq_diretoria_w::text <> '') then

	select	max(coalesce(QT_ENCERRADA_PESSOA,0)),
		max(coalesce(QT_OS_ENCERRADA_PF_ANO,0))
	into STRICT	qt_os_encerradas_w,
		qt_acumulado_w
	from	w_indicador_desenv_apres
	where	dt_referencia 		= dt_referencia_w
	and	nr_seq_diretoria	= nr_seq_diretoria_w
	and	ie_abrangencia 		= 'DIRE';

	CALL PPM_GRAVAR_RESULTADO(nr_seq_objetivo_metrica_p,dt_referencia_p,qt_os_encerradas_w, 0, 0, nm_usuario_p);

	select	max(nr_sequencia)
	into STRICT	nr_seq_acumulado_w
	from	ppm_objetivo_result a
	where	a.nr_seq_metrica	= nr_seq_objetivo_metrica_p
	and	trunc(dt_referencia)	= dt_fim_ano_w;

	update	ppm_objetivo_result
	set	vl_resultado_calc	= qt_acumulado_w
	where	nr_sequencia		= nr_seq_acumulado_w;

elsif (nm_usuario_exec_w IS NOT NULL AND nm_usuario_exec_w::text <> '') then

	--Busca as OSs encerradas com base no indicador j√° utilizado
	select	sum(a.qt_ordem)
	into STRICT	qt_os_encerradas_w
	from	w_avaliacao_usuario a
	where	a.dt_referencia between dt_ref_inicio_w and dt_ref_fim_w
	and	a.ie_referencia = 'E'
	and	a.nm_usuario 	= nm_usuario_exec_w;

	CALL PPM_GRAVAR_RESULTADO(nr_seq_objetivo_metrica_p, dt_referencia_p, qt_os_encerradas_w, qt_os_encerradas_w, qt_os_encerradas_w, nm_usuario_p);
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE ppm_metrica_os_encerrada ( nr_seq_metrica_p bigint, nr_seq_objetivo_metrica_p bigint, cd_pessoa_fisica_p text, nm_usuario_p text, dt_referencia_p timestamp) FROM PUBLIC;

