CREATE OR REPLACE PROCEDURE preparar_processo_adep (nr_seq_processo_p bigint, ie_preparar_p text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_regra_w		bigint;
ie_conferencia_w	varchar(1);
ie_funcao_p         varchar(1);
cd_funcao_ativa_w   integer;


BEGIN
if (nr_seq_processo_p IS NOT NULL AND nr_seq_processo_p::text <> '') and (ie_preparar_p IS NOT NULL AND ie_preparar_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then

	if (ie_preparar_p = 'S') then

		/*
		update	adep_processo
		set	dt_preparo		= sysdate,
			nm_usuario_preparo	= nm_usuario_p,
			nm_usuario		= nm_usuario_p
		where	nr_sequencia		= nr_seq_processo_p
		and	dt_preparo		is null
		and	nm_usuario_preparo	is null;

		update	adep_processo_frac
		set	dt_processo		= sysdate,
			nm_usuario_processo	= nm_usuario_p
		where	nr_seq_processo		= nr_seq_processo_p
		and	dt_processo		is null
		and	nm_usuario_processo	is null
		and	ie_status_frac		<> 'C';
		*/
		CALL atualiza_status_processo_adep(nr_seq_processo_p, null, 'A', 'P', clock_timestamp(), nm_usuario_p);

		select	max(nr_seq_regra)
		into STRICT	nr_seq_regra_w
		from	adep_processo
		where	nr_sequencia = nr_seq_processo_p;

		if (nr_seq_regra_w IS NOT NULL AND nr_seq_regra_w::text <> '') then

			select	coalesce(max(ie_conferencia),'S')
			into STRICT	ie_conferencia_w
			from	regra_etapa_gedipa
			where	nr_sequencia = nr_seq_regra_w;

			ie_funcao_p := 'G';

			cd_funcao_ativa_w := obter_funcao_ativa;
			if (cd_funcao_ativa_w = 88) or (cd_funcao_ativa_w = 1113) then
				ie_funcao_p := 'A';
			end if;

			if (ie_conferencia_w = 'N') then
				CALL atualiza_status_processo_adep(nr_seq_processo_p, null, ie_funcao_p, 'C', clock_timestamp(), nm_usuario_p);
			end if;
		end if;

	elsif (ie_preparar_p = 'N') then

		/*
		update	adep_processo
		set	dt_preparo		= null,
			nm_usuario_preparo	= null,
			ie_status_processo		= 'L',
			nm_usuario		= nm_usuario_p
		where	nr_sequencia		= nr_seq_processo_p
		and	dt_preparo		is not null
		and	nm_usuario_preparo	is not null;

		update	adep_processo_frac
		set	dt_processo		= null,
			nm_usuario_processo	= null,
			ie_status_frac		= 'F'
		where	nr_seq_processo		= nr_seq_processo_p
		and	dt_processo		is not null
		and	nm_usuario_processo	is not null
		and	ie_status_frac		<> 'C';
		*/
		CALL atualiza_status_processo_adep(nr_seq_processo_p, null, 'A', 'RP', clock_timestamp(), nm_usuario_p);

	end if;

end if;

if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
