SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE prescr_gasoterapia_beforepost ( nr_seq_gas_p bigint, ie_disp_resp_esp_p text, ie_unidade_medida_p text, qt_gasoterapia_p bigint, nr_atendimento_p bigint, dt_prescricao_p timestamp, nr_prescricao_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text, nr_dia_util_p INOUT bigint ) AS $body$
DECLARE


nr_dia_util_w			bigint;
qt_max_permitida_w		bigint;

BEGIN

if (nr_seq_gas_p IS NOT NULL AND nr_seq_gas_p::text <> '') then
	begin
    
	select	max(qt_max_permitida)
	into STRICT	qt_max_permitida_w
	from	regra_unidade_gas
	where	nr_seq_gas = nr_seq_gas_p
	and 	ie_disp_resp_esp = ie_disp_resp_esp_p
	and	ie_unidade_medida = ie_unidade_medida_p;
	end;
	
	if (qt_max_permitida_w <> 0) and (qt_max_permitida_w < qt_gasoterapia_p) then
		begin
		---O Dispositivo selecionado nao pode ser prescrito com unidade de medida superior a QT_GASOTERAPIA IE_UNIDADE_MEDIDA
		CALL Wheb_mensagem_pck.exibir_mensagem_abort(nr_seq_mensagem_p => 48799, vl_macros_p => 'QT_GASOTERAPIA='|| qt_max_permitida_w || ';IE_UNIDADE_MEDIDA= ' || ie_unidade_medida_p);
		end;
	end if;
end if;


if (nr_seq_gas_p IS NOT NULL AND nr_seq_gas_p::text <> '') then
	begin
	
	select  coalesce(max(a.nr_dia_util),0) + 1
	into STRICT	nr_dia_util_w
	from    prescr_gasoterapia a,
		prescr_medica b
	where   a.nr_prescricao = b.nr_prescricao
	and     trunc(b.dt_prescricao) = trunc(to_date(dt_prescricao_p,'dd/mm/yyyy  hh24:mi:ss') - 1)
	and     b.nr_atendimento = nr_atendimento_p
	and     a.nr_seq_gas = nr_seq_gas_p
	and     b.nr_prescricao          <> nr_prescricao_p
	and     coalesce(b.dt_suspensao::text, '') = ''
	and     coalesce(a.dt_suspensao::text, '') = '';	
	
	end;
end if;

nr_dia_util_p := nr_dia_util_w;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
