SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE prescr_procedimento_afterpost ( nr_prescricao_p bigint, nr_sequencia_p bigint, nr_seq_prot_glip_p bigint, nr_seq_proc_interno_p bigint, cd_procedimento_p text, ie_gerar_prescr_glic_p text, ie_origem_proced_p bigint, ie_consistir_proced_p text, cd_perfil_p bigint, nm_usuario_p text, ie_gerar_itens_assoc_p text, ie_exige_laudo_p INOUT text, ie_setor_proc_p INOUT text, ds_erro_p INOUT text, ie_nr_seq_alt_p text) AS $body$
DECLARE

		 
ie_exige_laudo_w		varchar(1);
ie_setor_proc_w			varchar(1);
ie_excluir_w			varchar(1);
ds_erro_w				varchar(255);	
nr_seq_exame_w			bigint;
qt_w					bigint;
nr_seq_interno_w		bigint;
qt_itens_assoc_glic_w	bigint;

			 

BEGIN 
if (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') and (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then 
	begin 
	--gerar_med_mat_assoc(nr_prescricao_p,nr_sequencia_p); 
	CALL gerar_prescr_proc_assoc(nr_prescricao_p,nr_sequencia_p, nm_usuario_p);
	 
	if (nr_seq_prot_glip_p IS NOT NULL AND nr_seq_prot_glip_p::text <> '') and (coalesce(ie_nr_seq_alt_p,'S') = 'S') then 
		select	count(*) 
		into STRICT 	qt_itens_assoc_glic_w 
		from  	prescr_material 
		where 	ie_agrupador	 = 5 
		and		(ie_glicemia IS NOT NULL AND ie_glicemia::text <> '') 
		and	  	nr_prescricao 	 = nr_prescricao_p 
		and  	nr_sequencia_proc = nr_sequencia_p;
		 
		if (qt_itens_assoc_glic_w > 0) then 
				 
			delete 
			from	prescr_material 
			where	ie_agrupador = 5 
			and		(ie_glicemia IS NOT NULL AND ie_glicemia::text <> '') 
			and	  	nr_prescricao = nr_prescricao_p 
			and		nr_sequencia_proc = nr_sequencia_p;
				 
			commit;
		end if;
		CALL gerar_med_mat_assoc_glic(nr_prescricao_p,nr_sequencia_p,nr_seq_prot_glip_p);
	end if;
	 
	select	max(ie_exige_laudo) 
	into STRICT	ie_exige_laudo_w 
	from	procedimento 
	where	cd_procedimento		= cd_procedimento_p 
	and		ie_origem_proced	= ie_origem_proced_p;
	 
	if (ie_gerar_itens_assoc_p = 'S') then 
		CALL gerar_med_mat_assoc(nr_prescricao_p, nr_sequencia_p);
	end if;
	 
	if (nr_seq_proc_interno_p IS NOT NULL AND nr_seq_proc_interno_p::text <> '') then 
		select	coalesce(max(ie_excluir_proc_princ),'N') 
		into STRICT	ie_excluir_w 
		from	proc_interno 
		where	nr_sequencia = nr_seq_proc_interno_p;
		 
		select	max(nr_seq_interno) 
		into STRICT	nr_seq_interno_w 
		from	prescr_procedimento 
		where	nr_prescricao	= nr_prescricao_p 
		and		nr_sequencia	= nr_sequencia_p;
		 
		select	count(*) 
		into STRICT	qt_w 
		from	prescr_Procedimento 
		where	nr_prescricao	= nr_prescricao_p 
		and		nr_seq_proc_excl = nr_seq_interno_w;
 
		if (qt_w > 0) and (ie_excluir_w = 'S') then 
			CALL gera_excl_proced_principal(nr_prescricao_p,nr_seq_proc_interno_p);
		end if;
	end if;
	 
	CALL reordenar_procedimento(nr_sequencia_p);
	 
	if (ie_gerar_prescr_glic_p = 'S') then 
		CALL gerar_prescr_proc_glic(nr_prescricao_p,nr_sequencia_p,nr_seq_prot_glip_p,nm_usuario_p);
	end if;
	 
	CALL ajustar_prescr_mat_proc(nr_prescricao_p,nr_sequencia_p, cd_perfil_p,null);
	 
	if (ie_consistir_proced_p = 'S') then 
		ds_erro_w := consistir_prescr_procedimento(nr_prescricao_p, nr_sequencia_p, nm_usuario_p, cd_perfil_p, ds_erro_w);
	end if;
	 
	select	max(nr_seq_exame) 
	into STRICT	nr_seq_exame_w 
	from	prescr_procedimento 
	where	nr_prescricao	= nr_prescricao_p 
	and		nr_sequencia	= nr_sequencia_p;	
	 
	ie_setor_proc_w	:=	obter_se_questiona_setor_proc(cd_procedimento_p,ie_origem_proced_p,nr_seq_proc_interno_p, nr_seq_exame_w);
	end;
end if;
 
ie_exige_laudo_p	:= ie_exige_laudo_w;
ie_setor_proc_p		:= ie_setor_proc_w;
ds_erro_p 		:= ds_erro_w;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
