CREATE OR REPLACE PROCEDURE prescr_proc_newrec_abort_eup ( cd_convenio_p bigint, nr_atendimento_cat_p bigint, cd_estabelecimento_p bigint, ie_obter_se_exige_cpf_p text, nr_atendimento_p bigint, cd_pessoa_fisica_p text, cd_pessoa_responsavel_p text, nm_usuario_p text, ds_msg_erro_p INOUT text, ie_pasta_cadastro_p INOUT text, nr_seq_interno_p INOUT bigint, ie_exige_cpf_paciente_p INOUT text) AS $body$
DECLARE

 
ds_msg_erro_w				varchar(255);
ie_idade_pf_w				bigint;
cd_pessoa_cpf_w				varchar(10) := cd_pessoa_fisica_p;
ds_compl_pf_w				varchar(30);
ie_brasileiro_w				varchar(1);
ie_exige_cpf_w				varchar(1);
ie_direciona_cadastro_w		varchar(1);
ie_acesso_pasta_cad_w		varchar(1);


BEGIN 
ie_exige_cpf_w := Obter_param_Usuario(916, 501, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_exige_cpf_w);
ie_direciona_cadastro_w := Obter_param_Usuario(916, 701, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_direciona_cadastro_w);
ie_acesso_pasta_cad_w := Obter_param_Usuario(916, 919, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_acesso_pasta_cad_w);
 
SELECT * FROM consiste_lab_convenio_js(cd_convenio_p, nr_atendimento_cat_p, cd_estabelecimento_p, ie_obter_se_exige_cpf_p, nr_atendimento_p, ie_exige_cpf_paciente_p, nr_seq_interno_p, ds_msg_erro_w ) INTO STRICT ie_exige_cpf_paciente_p, nr_seq_interno_p, ds_msg_erro_w;
	 
if (ie_exige_cpf_w = 'S') then 
	begin 
	ie_idade_pf_w := obter_idade_pf(cd_pessoa_fisica_p, clock_timestamp(), 'A');
 
	if (ie_idade_pf_w < 18 and 
		(cd_pessoa_responsavel_p IS NOT NULL AND cd_pessoa_responsavel_p::text <> '')) then 
		cd_pessoa_cpf_w := cd_pessoa_responsavel_p;
	end if;
	 
	select	trim(both coalesce(nr_cpf,obter_compl_pf(cd_pessoa_fisica,3,'C'))), 
			obter_se_brasileiro(cd_pessoa_fisica) 
	into STRICT	ds_compl_pf_w, 
			ie_brasileiro_w 
	from	pessoa_fisica 
	where	cd_pessoa_fisica = cd_pessoa_cpf_w;
	 
	if (ie_exige_cpf_paciente_p = 'S' and 
		ds_compl_pf_w <> '' and 
		ie_brasileiro_w = 'S') then 
		begin 
		ie_pasta_cadastro_p := 'N';
		if (ie_direciona_cadastro_w = 'S' and 
			ie_acesso_pasta_cad_w = 'S') then 
			ie_pasta_cadastro_p := 'S';
		end if;
		ds_msg_erro_p := ds_msg_erro_w;
		end;
	end if;
	end;
end if;
 
commit;
 
end;
$body$
