CREATE OR REPLACE PROCEDURE prescr_solucao_before_post ( nr_prescricao_p bigint, nr_seq_solucao_p bigint, nr_sequencia_p bigint, ie_inserir_elem_p text, ie_calcular_dialise_p text, nm_usuario_p text) AS $body$
DECLARE

 
qt_aux_w	integer;
		

BEGIN 
 
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_solucao_p IS NOT NULL AND nr_seq_solucao_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then 
	begin 
	 
	 
	select	count(cd_material) 
	into STRICT	qt_aux_w 
	from	prescr_material 
	where	nr_prescricao = nr_prescricao_p 
	and		nr_sequencia_solucao = nr_seq_solucao_p 
	and		ie_agrupador = 13;
		 
	if (ie_inserir_elem_p = 'S') and (qt_aux_w = 0) then 
		begin 
		 
		CALL inserir_elem_prot_hemodialise( 
			nr_prescricao_p, 
			nr_seq_solucao_p, 
			nm_usuario_p);
		end;
	end if;
	 
	CALL calcular_elem_hemodialise( 
		nr_prescricao_p, 
		nr_seq_solucao_p, 
		nm_usuario_p);
 
	if (ie_calcular_dialise_p = 'S') then 
		begin 
		 
		CALL calcular_solucao_protocolo( 
			nr_sequencia_p, 
			nr_prescricao_p, 
			nr_seq_solucao_p);
		end;
	end if;
	end;
end if;
commit;
end;
$body$
