CREATE OR REPLACE PROCEDURE proced_atribchange_cdintervalo ( cd_intervalo_p text, ie_lado_p text, ie_horario_proced_p text, nr_prescricao_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint, nr_seq_proc_interno_p bigint, nr_atendimento_p bigint, ie_operacao_p INOUT text, cd_intervalo_acm_p INOUT text, dt_prev_execucao_p INOUT timestamp, nr_ocorrencia_p INOUT bigint, qt_procedimento_p INOUT bigint, nm_usuario_p text) AS $body$
DECLARE

 
ie_operacao_w		varchar(1);
dt_inicio_prescr_w	timestamp;
cd_intervalo_acm_w	varchar(7);
nr_ocorrencia_w		smallint;
dt_prev_execucao_w	timestamp;
qt_procedimento_w	double precision;


BEGIN 
if (cd_intervalo_p IS NOT NULL AND cd_intervalo_p::text <> '') and (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then 
	begin 
	select	max(ie_operacao)  
	into STRICT	ie_operacao_w 
	from	intervalo_prescricao 
	where	cd_intervalo =	cd_intervalo_p;
	 
	if (ie_horario_proced_p = 'S') then 
		begin 
		select	dt_inicio_prescr 
		into STRICT	dt_inicio_prescr_w 
		from	prescr_medica 
		where	nr_prescricao	= nr_prescricao_p;
		 
		dt_prev_execucao_w := obter_horario_proced_interv(cd_intervalo_p, nr_prescricao_p, dt_inicio_prescr_w, dt_inicio_prescr_w);
		end;
	end if;
 
	select 	max(cd_intervalo)  
	into STRICT	cd_intervalo_acm_w 
	from	intervalo_prescricao 
	where 	ie_acm 		= 'S' 
	and 	ie_situacao 	= 'A';
 
	select	coalesce(max(qt_se_necessario),1) 
	into STRICT	nr_ocorrencia_w 
	from	intervalo_prescricao 
	where	cd_intervalo = cd_intervalo_p;
	 
	qt_procedimento_w	:= obter_quantidade_prescr_proced(cd_intervalo_p, ie_lado_p, cd_procedimento_p,ie_origem_proced_p,nr_seq_proc_interno_p,nr_atendimento_p);
	end;
end if;
ie_operacao_p		:= ie_operacao_w;
cd_intervalo_acm_p	:= cd_intervalo_acm_w;
dt_prev_execucao_p	:= dt_prev_execucao_w;
nr_ocorrencia_p		:= nr_ocorrencia_w;
qt_procedimento_p	:= qt_procedimento_w;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
