SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE processar_conta ( nr_interno_conta_p bigint, ie_separacao_conta_p text, nm_usuario_p text) AS $body$
DECLARE


ds_locale_w		establishment_locale.ds_locale%type;
cd_estabelecimento_w	conta_paciente.cd_estabelecimento%type;
cd_convenio_w		conta_paciente.cd_convenio_parametro%type;
dt_periodo_inicial_w	conta_paciente.dt_periodo_inicial%type;
nr_seq_regra_deducao_w	conv_regra_deducao.nr_sequencia%type;
ds_procedure_w		varchar(30)	:= 'PROCESSAR_CONTA_MX';
qt_processada_w		bigint;
				

BEGIN
/* This procedure was modified to consider the new process of internationalization. It was created two new procedures, are they: 
PROCESSAR_CONTA_MX and PROCESSAR_CONTA_KSA, where the system will verify the system localization and call the correct procedure according to the business country.*/
ds_locale_w := pkg_i18n.get_user_locale();

if (ds_locale_w = 'es_AR') then
	CALL processar_conta_arg(nr_interno_conta_p, nm_usuario_p);
else
	select	max(cd_estabelecimento),
		max(cd_convenio_parametro),
		max(dt_periodo_inicial)
	into STRICT	cd_estabelecimento_w,
		cd_convenio_w,
		dt_periodo_inicial_w
	from	conta_paciente
	where	nr_interno_conta = nr_interno_conta_p;

	nr_seq_regra_deducao_w	:= coalesce(obter_regra_deducao_conv(cd_estabelecimento_w, cd_convenio_w, null, dt_periodo_inicial_w),0);

	if (nr_seq_regra_deducao_w > 0) then
		ds_procedure_w	:= 'PROCESSAR_DEDUCAO_CONV_CONTA';
	end if;
	
	select	count(a.nr_sequencia)
	into STRICT	qt_processada_w
	from	conta_pac_deducao_conv a
	where	(a.dt_processamento IS NOT NULL AND a.dt_processamento::text <> '')
	and	nr_seq_conta_orig = nr_interno_conta_p;

	if (qt_processada_w = 0) then
		if (ds_locale_w = 'es_MX') then
			EXECUTE get_procedure_call(ds_procedure_w, 3, ds_locale_w) using nr_interno_conta_p, ie_separacao_conta_p, nm_usuario_p;
		elsif (ds_locale_w = 'es_CO') then
			CALL processar_conta_col(nr_interno_conta_p, nm_usuario_p,'P');
		end if;
	end if;

	if (qt_processada_w > 0) then
		update	conta_pac_deducao_conv
		set	dt_atualizacao = clock_timestamp(),
			nm_usuario = nm_usuario_p,
			nr_seq_conta_des  = NULL,
			dt_processamento  = NULL,
			vl_calculado  = NULL,
			vl_calculado_com_imp  = NULL,
			vl_calculado_sem_imp  = NULL
		where	nr_seq_conta_orig = nr_interno_conta_p;
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
