CREATE OR REPLACE PROCEDURE proc_mover_atividade_sprint ( nr_seq_cadastro_sprint_p CADASTRO_SPRINT_ATIVIDADE.nr_seq_cadastro_sprint%type, nr_sequencia_p CADASTRO_SPRINT_ATIVIDADE.ds_atividade%type ) AS $body$
BEGIN
    update CADASTRO_SPRINT_ATIVIDADE
        set nr_seq_cadastro_sprint = nr_seq_cadastro_sprint_p
        where nr_sequencia IN (WITH RECURSIVE cte AS (

            SELECT regexp_substr(nr_sequencia_p, '[^,]+', 1, level) AS nr_sequencia
            
            (regexp_substr(nr_sequencia_p, '[^,]+', 1, level) IS NOT NULL AND (regexp_substr(nr_sequencia_p, '[^,]+', 1, level))::text <> '')  UNION ALL

            SELECT regexp_substr(nr_sequencia_p, '[^,]+', 1, level) AS nr_sequencia
            
            (regexp_substr(nr_sequencia_p, '[^,]+', 1, level) IS NOT NULL AND (regexp_substr(nr_sequencia_p, '[^,]+', 1, level))::text <> '') JOIN cte c ON ()

) SELECT * FROM cte;
);

    COMMIT;

END;
$body$
