SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE proc_rotina_consiste_lado ( ie_lado_p text, ie_exige_lado_p text, ds_exige_lado_p INOUT text ) AS $body$
DECLARE

				
ie_consiste_lado_w varchar(1);
ds_exige_lado_w    varchar(255);


BEGIN
	ie_consiste_lado_w := Obter_Param_Usuario(820, 77, obter_perfil_ativo, obter_usuario_ativo, obter_estabelecimento_ativo, ie_consiste_lado_w);
	
	if (ie_consiste_lado_w = 'S') then -- 77
		if (coalesce(ie_lado_p::text, '') = '') and (ie_exige_lado_p = 'S' or ie_exige_lado_p = 'L') then
			ds_exige_lado_w	:= substr(obter_texto_tasy(32711, wheb_usuario_pck.get_nr_seq_idioma),1,255);
		end if;
		
		if (ie_exige_lado_p = 'L') and (ie_lado_p = 'A') then
			ds_exige_lado_w	:= substr(obter_texto_tasy(1022971, wheb_usuario_pck.get_nr_seq_idioma),1,255);
		end if;
	end if;
	
	ds_exige_lado_p := ds_exige_lado_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
