CREATE OR REPLACE PROCEDURE proc_update_controll_code ( nr_sequencia_p NOTA_FISCAL.NR_SEQUENCIA%type, cd_codigo_control_p NOTA_FISCAL.CD_CONTROL_CODE%type, nr_autorizacao_p NOTA_FISCAL.NR_AUTORIZACAO%type ) AS $body$
BEGIN
IF (cd_codigo_control_p IS NOT NULL AND cd_codigo_control_p::text <> '' AND nr_autorizacao_p IS NOT NULL AND nr_autorizacao_p::text <> '') THEN

    UPDATE nota_fiscal 
    SET    nr_autorizacao    = nr_autorizacao_p,
           cd_control_code   = cd_codigo_control_p
    WHERE  nr_sequencia      = nr_sequencia_p;

    COMMIT;
END IF;
END;
$body$
