CREATE OR REPLACE PROCEDURE proc_update_reason_cancell ( nr_sequencia_p AUTORIZACAO_CONVENIO.NR_SEQUENCIA%type, ds_motivo_cancelamento_p AUTORIZACAO_CONVENIO.DS_MOTIVO_CANCELAMENTO%type ) AS $body$
