SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE proj_ajustar_dt_termino (nr_seq_projeto_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


dt_fim_prev_w		timestamp;
dt_inicio_prev_w	timestamp;
qt_dias_prev_w		bigint;
qt_dias_w		bigint;
qt_dias_uteis_w		bigint;
nr_seq_gerencia_w	bigint;
dt_referencia_w		timestamp;


BEGIN

if (nr_seq_projeto_p IS NOT NULL AND nr_seq_projeto_p::text <> '') then
	select	trunc(dt_inicio_prev,'dd'),
		dividir(proj_obter_qt_total_horas_cron(nr_sequencia, 0, 'T', 'E'), 8),
		dt_fim_prev,
		nr_seq_gerencia
	into STRICT	dt_inicio_prev_w,
		qt_dias_prev_w,
		dt_fim_prev_w,
		nr_seq_gerencia_w
	from	proj_projeto
	where	nr_sequencia	= nr_seq_projeto_p;

	if (coalesce(dt_fim_prev_w::text, '') = '') and (nr_seq_gerencia_w not in (3,7)) and (dt_inicio_prev_w IS NOT NULL AND dt_inicio_prev_w::text <> '') then /*and
		(qt_dias_prev_w > 0) then*/
		qt_dias_w	:= 0;
		qt_dias_uteis_w	:= 0;
		while(qt_dias_uteis_w < qt_dias_prev_w) loop

			qt_dias_w	:= qt_dias_w + 1;

			dt_referencia_w	:= dt_inicio_prev_w + qt_dias_w;

			if (dt_referencia_w = obter_proximo_dia_util(cd_estabelecimento_p,dt_referencia_w)) then
				qt_dias_uteis_w	:= qt_dias_uteis_w + 1;
			end if;
		end loop;

		if (qt_dias_w > 0) then
			dt_fim_prev_w	:= dt_inicio_prev_w + qt_dias_w;
		else
			dt_fim_prev_w	:= dt_inicio_prev_w; -- significa que é um projeto curto (casos que podem ocorrer na migração) que pode ser começado e terminado no mesmo dia.
		end if;

		if (dt_fim_prev_w IS NOT NULL AND dt_fim_prev_w::text <> '') then
			update	proj_projeto
			set	dt_fim_prev	= dt_fim_prev_w
			where	nr_sequencia	= nr_seq_projeto_p;
		end if;
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
