SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE proj_ajuste_ficha_tecnica () AS $body$
DECLARE


nr_seq_projeto_w	bigint;
qt_registros_w		bigint;
ds_objetivo_w		varchar(2000);

C01 CURSOR FOR
SELECT 	nr_sequencia,
	ds_objetivo
from	proj_projeto
where	(ds_objetivo IS NOT NULL AND ds_objetivo::text <> '');



BEGIN

open C01;
loop
fetch C01 into
	nr_seq_projeto_w,
	ds_objetivo_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

		select 	count(*)
		into STRICT	qt_registros_w
		from	proj_ficha_projeto
		where	nr_seq_projeto = nr_seq_projeto_w;


		if (qt_registros_w = 0) then
			insert into proj_ficha_projeto(NR_SEQUENCIA,
							DT_ATUALIZACAO,
							NM_USUARIO,
							DT_ATUALIZACAO_NREC,
							NM_USUARIO_NREC,
							NR_SEQ_PROJETO,
							DS_OBJETIVO)
			values (nextval('proj_ficha_projeto_seq'),
							clock_timestamp(),
							'Tasy',
							clock_timestamp(),
							'Tasy',
							nr_seq_projeto_w,
							ds_objetivo_w);
		else
			select 	count(*)
			into STRICT	qt_registros_w
			from	proj_ficha_projeto
			where	nr_seq_projeto = nr_seq_projeto_w
			and	coalesce(ds_objetivo::text, '') = '';

			if (qt_registros_w > 0 ) then
				update 	proj_ficha_projeto
				set	ds_objetivo = ds_objetivo_w
				where	nr_seq_projeto = nr_seq_projeto_w
				and	coalesce(ds_objetivo::text, '') = '';
			end if;
		end if;


	end;
end loop;
close C01;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE proj_ajuste_ficha_tecnica () FROM PUBLIC;

