SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE proj_alocar_recurso_etapa ( nr_seq_etapa_p bigint, cd_programador_p text, nm_usuario_p text ) AS $body$
DECLARE


nr_seq_projeto_w		bigint;
nr_seq_equipe_papel_w	bigint;


BEGIN

	select	max(c.nr_seq_proj)
	into STRICT	nr_seq_projeto_w
	from	proj_cron_etapa e,
			proj_cronograma c
	where	e.nr_seq_cronograma = c.nr_sequencia
	and		e.nr_sequencia = nr_seq_etapa_p;

	select  min(c.nr_sequencia) nr_seq_equipe_papel
	into STRICT    nr_seq_equipe_papel_w
	from    usuario a,
			pessoa_fisica b,
			proj_equipe_papel c,
			proj_equipe d
	where   a.cd_pessoa_fisica = b.cd_pessoa_fisica
	and     b.cd_pessoa_fisica = c.cd_pessoa_fisica
	and     c.nr_seq_equipe = d.nr_sequencia
	and     d.nr_seq_proj = nr_seq_projeto_w
	and     a.cd_pessoa_fisica = cd_programador_p;

	if (nr_seq_etapa_p IS NOT NULL AND nr_seq_etapa_p::text <> '')
		and (cd_programador_p IS NOT NULL AND cd_programador_p::text <> '') then

		insert into proj_cron_etapa_equipe(
				nr_sequencia,
				nr_seq_etapa_cron,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				nr_seq_equipe,
				nr_seq_equipe_papel,
				ie_recurso_externo,
				cd_programador)
		values (	nextval('proj_cron_etapa_equipe_seq'),
				nr_seq_etapa_p,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				null,
				nr_seq_equipe_papel_w,
				'N',
				cd_programador_p);

	end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
