SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE proj_alocar_recurso ( nm_usuario_p text, nr_seq_etapa_p bigint, cd_programador_p text) AS $body$
DECLARE


nr_seq_etapa_w	bigint;
qt_registros_w	bigint;

C01 CURSOR FOR
	SELECT	a.nr_sequencia
	from	proj_cron_etapa a
	where	nr_seq_superior = nr_seq_etapa_p;


BEGIN


open C01;
loop
fetch C01 into
	nr_seq_etapa_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

		select 	count(*)
		into STRICT	qt_registros_w
		from	proj_cron_etapa
		where	nr_seq_superior = nr_seq_etapa_w;

		if (qt_registros_w = 0) then
			select 	count(*)
			into STRICT	qt_registros_w
			from	proj_cron_etapa_equipe
			where	nr_seq_etapa_cron = nr_seq_etapa_w;


			if (qt_registros_w = 0) then

				insert into proj_cron_etapa_equipe(
					nr_sequencia,
					nr_seq_etapa_cron,
					dt_atualizacao,
					nm_usuario,
					dt_atualizacao_nrec,
					nm_usuario_nrec,
					nr_seq_equipe,
					nr_seq_equipe_papel,
					ie_recurso_externo,
					cd_programador)
				values (	nextval('proj_cron_etapa_equipe_seq'),
					nr_seq_etapa_w,
					clock_timestamp(),
					nm_usuario_p,
					clock_timestamp(),
					nm_usuario_p,
					null,
					null,
					'N',
					cd_programador_p);
			elsif (qt_registros_w = 1) then
					update	proj_cron_etapa_equipe
					set		cd_programador	= cd_programador_p,
							dt_atualizacao	= clock_timestamp(),
							nm_usuario		= nm_usuario_p
					where	nr_seq_etapa_cron = nr_seq_etapa_w;
			end if;
		else
			CALL PROJ_ALOCAR_RECURSO(nm_usuario_p,nr_seq_etapa_w,cd_programador_p);
		end if;
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
