SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE proj_alterar_escopo (nr_seq_alt_escopo_p bigint, nr_seq_cronograma_p bigint, nr_seq_projeto_p bigint, ie_opcao_p text default null) AS $body$
DECLARE


qt_horas_acres_w		double precision;
qt_horas_anterior_w	double precision;
nr_seq_etapa_cron_w	double precision;
qt_horas_total_w		double precision;

c01 CURSOR FOR
SELECT	sum( b.qt_horas_acres * CASE WHEN b.ie_soma_subtrai='N' THEN  -1  ELSE 1 END  ) qt_horas_acres,
	b.nr_seq_etapa_cron
from	proj_alteracao_escopo a,
	proj_alt_escopo_ativ b
where	a.nr_sequencia = b.nr_seq_alt_escopo
and	a.nr_seq_cronograma = nr_seq_cronograma_p  --230
and	a.nr_seq_projeto = nr_seq_projeto_p  --167
and	b.nr_seq_alt_escopo = nr_seq_alt_escopo_p  --40
group by a.nr_seq_cronograma, a.nr_sequencia, b.nr_seq_etapa_cron;


BEGIN

	open c01;
	loop
	fetch c01 into
	qt_horas_acres_w,
	nr_seq_etapa_cron_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		select	coalesce(max(qt_hora_prev), -1)
		into STRICT	qt_horas_anterior_w
		from	proj_cron_etapa
		where	nr_seq_cronograma = nr_seq_cronograma_p --230
		and	nr_sequencia = nr_seq_etapa_cron_w;

		qt_horas_total_w := (qt_horas_anterior_w + qt_horas_acres_w);

		update	proj_cron_etapa
		set	qt_hora_prev = qt_horas_total_w
		where	nr_sequencia = nr_seq_etapa_cron_w
		and 	qt_horas_anterior_w <> -1;
		end;
	end loop;
	close c01;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
