CREATE OR REPLACE PROCEDURE proj_aprovar_posic_dia (nr_seq_proj_p bigint, ie_acao_p text, dt_referencia_p timestamp) AS $body$
BEGIN

if (ie_acao_p = 'A') then

	update	proj_posicao_coordenacao
	set	dt_aprovacao  = clock_timestamp()
	where	nr_seq_proj = nr_seq_proj_p
	and	dt_posicao = dt_referencia_p;

elsif (ie_acao_p = 'D') then

	update	proj_posicao_coordenacao
	set	dt_aprovacao   = NULL
	where	nr_seq_proj = nr_seq_proj_p
	and	dt_posicao = dt_referencia_p;

end if;

commit;


end;
