CREATE OR REPLACE PROCEDURE proj_ata_inativar_ata (nm_usuario_p text, nr_sequencia_p bigint, ie_opcao_p text) AS $body$
BEGIN
	if (ie_opcao_p = 'I') then
	begin
		update 	proj_ata
		set	ie_situacao = 'I' ,
			nm_usuario = nm_usuario_p,
			dt_atualizacao = clock_timestamp()
		where 	nr_sequencia = nr_sequencia_p;
	end;
	elsif (ie_opcao_p = 'A') then
	begin
		update 	proj_ata
		set	ie_situacao = 'A' ,
			nm_usuario = nm_usuario_p,
			dt_atualizacao = clock_timestamp()
		where 	nr_sequencia = nr_sequencia_p;
	end;
end if;
commit;

end;
