CREATE OR REPLACE PROCEDURE proj_atualizar_acao_ficha ( nr_seq_ficha_p bigint, ie_setor_p text, ie_acao_p text, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_existe_w	bigint;


BEGIN

select	count(*)
into STRICT	qt_existe_w
from	proj_ficha_projeto;

if (qt_existe_w > 0) then
	begin
	update	proj_ficha_projeto
	set	ie_field_marketing	= CASE WHEN ie_setor_p='F' THEN  ie_acao_p  ELSE ie_field_marketing END
	where	nr_sequencia = nr_seq_ficha_p;
	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
