SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE proj_atualizar_inicio_real_sup (nm_usuario_p text, nr_seq_etapa_p bigint) AS $body$
DECLARE


  nr_seq_sup_w       bigint;
  nr_etapas_dt_fim_w bigint;


BEGIN

  select max(nr_seq_superior)
    into STRICT nr_seq_sup_w
    from proj_cron_etapa
   where nr_sequencia = nr_seq_etapa_p;

  if (nr_seq_sup_w IS NOT NULL AND nr_seq_sup_w::text <> '') then

    update proj_cron_etapa
       set dt_inicio_real = (SELECT min(dt_inicio_real)
                               from proj_cron_etapa
                              where nr_seq_superior = nr_seq_sup_w)
     where nr_sequencia = nr_seq_sup_w;

    select count(1)
      into STRICT nr_etapas_dt_fim_w
      from proj_cron_etapa
     where nr_seq_superior = nr_seq_sup_w
       and coalesce(dt_fim_real::text, '') = '';

    if (nr_etapas_dt_fim_w = 0) then
    
      update proj_cron_etapa
         set dt_fim_real = (SELECT max(dt_fim_real)
                              from proj_cron_etapa
                             where nr_seq_superior = nr_seq_sup_w)
       where nr_sequencia = nr_seq_sup_w;

    end if;

    commit;

    CALL proj_atualizar_inicio_real_sup(nm_usuario_p, nr_seq_sup_w);

  end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
