SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE proj_atualizar_subprojeto ( nr_seq_cronograma_p bigint, nm_usuario_p text) AS $body$
DECLARE


pr_realizacao_w		proj_cronograma.pr_realizacao%type;
qt_total_horas_w		proj_cronograma.qt_total_horas%type;
qt_horas_realizado_w	proj_cronograma.qt_horas_realizado%type;
qt_horas_saldo_w		proj_cronograma.qt_horas_saldo%type;
dt_inicio_w		proj_cronograma.dt_inicio%type;
dt_fim_w			proj_cronograma.dt_fim%type;
nr_seq_proj_w		proj_cronograma.nr_seq_proj%type;
nr_seq_etapa_w		proj_cron_etapa.nr_sequencia%type;
pr_previsto_w		proj_cron_etapa.pr_previsto%type;
nr_seq_cronograma_w	proj_cronograma.nr_sequencia%type;
nr_seq_sub_proj_w	proj_cron_etapa.nr_seq_sub_proj%type;

c01 CURSOR FOR
	SELECT	nr_sequencia,
		nr_seq_cronograma,
		nr_seq_sub_proj
	from	proj_cron_etapa
	where	nr_seq_sub_proj = nr_seq_proj_w
	and	coalesce(ie_situacao,'A') = 'A';
	

BEGIN
select	max(nr_seq_proj)
into STRICT	nr_seq_proj_w
from	proj_cronograma
where	nr_sequencia = nr_seq_cronograma_p
and	ie_tipo_cronograma = 'E';

if (nr_seq_proj_w > 0) then
	select	obter_pr_real_projeto(nr_seq_proj_w),
		proj_obter_qt_total_horas_cron(nr_seq_proj_w, 0, 'T', null),
		proj_obter_qt_total_horas_cron(nr_seq_proj_w, 0, 'R', null),
		proj_obter_qt_total_horas_cron(nr_seq_proj_w, 0, 'S', null)
	into STRICT	pr_realizacao_w,
		qt_total_horas_w,
		qt_horas_realizado_w,
		qt_horas_saldo_w
	;
end if;

select	min(dt_inicio_real)
into STRICT	dt_inicio_w
from	proj_cron_etapa
where	nr_seq_cronograma = nr_seq_cronograma_p
and	(dt_inicio_real IS NOT NULL AND dt_inicio_real::text <> '');

select	max(x.dt_fim_real)
into STRICT	dt_fim_w
from	proj_cron_etapa x
where	x.nr_seq_cronograma = nr_seq_cronograma_p
and	not exists (	SELECT	1
			from	proj_cron_etapa a
			where	a.nr_seq_cronograma = nr_seq_cronograma_p
			and	coalesce(a.dt_fim_real::text, '') = '');

open C01;
loop
fetch C01 into
	nr_seq_etapa_w,
	nr_seq_cronograma_w,
	nr_seq_sub_proj_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	
	select obter_proj_atual_pr_previsto(nr_seq_sub_proj_w)
	into STRICT	pr_previsto_w
	;
	
	update	proj_cron_etapa
	set	pr_etapa	= pr_realizacao_w,
		qt_hora_prev	= qt_total_horas_w,
		qt_hora_real	= qt_horas_realizado_w,
		qt_hora_saldo	= qt_horas_saldo_w,
		dt_inicio_real	= dt_inicio_w,
		dt_fim_real	= dt_fim_w,
		pr_previsto 	= pr_previsto_w
	where	nr_sequencia 	= nr_seq_etapa_w;
	commit;

	CALL proj_atualizar_pr_realizada(nr_seq_cronograma_w, nr_seq_etapa_w, 0);
	
	CALL Atualizar_Horas_Etapa_Cron(nr_seq_etapa_w);
	
	CALL Atualizar_Total_Horas_Cron(nr_seq_cronograma_w);
	
	CALL proj_desenv_atual_perc_cron(nr_seq_cronograma_w, nm_usuario_p);
	
	end;
end loop;
close C01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
